INSERT INTO instruction (recipe_id, instruction_id, instruction, instruction_image) VALUES
    (1, 1, '1. 손질된 새우를 끓는 물에 데쳐 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00028_1.png'),
    (1, 2, '2. 연두부, 달걀, 생크림, 설탕에 녹인 무염버터를 믹서에 넣고 간 뒤 새우(1)를 함께 섞어 그릇에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00028_2.png'),
    (1, 3, '3. 시금치를 잘게 다져 혼합물 그릇(2)에 뿌리고 찜기에 넣고 중간 불에서 10분 정도 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00028_3.png'),
    (2, 1, '1. 부추는 깨끗이 씻어 물기를 제거하고, 5cm 길이로 썰고 부추에 날콩가루를 넣고 고루 섞이도록 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230206/20230206054820_1675673300714.jpg'),
    (2, 2, '2. 찜기에 면보를 깔고 부추를 넣은 후 김이 오르게 쪄서 파랗게 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230206/20230206054834_1675673314720.jpg'),
    (2, 3, '3. 저염간장에 다진 대파, 다진 마늘, 고춧가루, 요리당 , 참기름, 참깨를 섞어 양념장을 만들고 찐 부추는 그릇에 담아낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230206/20230206054908_1675673348152.jpg'),
    (3, 1, '1. 물기를 빼고 2cm 정도의 크기로 썰은 부추와 양파를 양념장에 섞어 양념속을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00031_1.png'),
    (3, 2, '2. 깨끗이 씻은 방울토마토는 꼭지를 떼고 윗부분에 칼로 십자모양으로 칼집을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00031_4.png'),
    (3, 3, '3. 칼집을 낸 방울토마토에 양념속을 사이사이에 넣어 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00031_5.png'),
    (4, 1, '1. 사과, 순두부를 믹서에 넣고 곱게 갈아 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00032_1.png'),
    (4, 2, '2. 오이는 소금으로 문질러 씻어 반을 갈라 씨를 제거하고 어슷썰기를 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00032_2.png'),
    (4, 3, '3. 썰어 놓은 오이에 순두부사과 소스를 넣고 버무린 후 다진 땅콩을 뿌려 마무리 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00032_3.png'),
    (5, 1, '1. 북어채를 잘게 손으로 찢어 찬물에 헹구어 준비한 후 양파, 표고버섯, 사과는 채 썰고 새우는 등쪽의 두세 마디에 꼬챙이를 넣어 내장을 뺀 후 헹군다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_2.png'),
    (5, 2, '2. 북어채를 잘게 손으로 찢어 찬물에 헹구어 준비한 후 양파, 표고버섯, 사과는 채 썰고 새우는 등쪽의 두세 마디에 꼬챙이를 넣어 내장을 뺀 후 헹군다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_2.png'),
    (5, 3, '3. 찬물에 북어채, 새우, 표고버섯, 양파를 넣고 20분 정도 끓인 후 사과를 넣어 북어의 씁쓸한 맛을 없앤다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_3.png'),
    (5, 5, '5. 그릇에 담아낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_5.png'),
    (6, 1, '1. 황태는 손질하여 물에 헹궈 건져 놓고 콩나물은 다듬어 씻고 청양고추는 어슷썰기 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_3.png'),
    (6, 2, '1. 황태는 손질하여 물에 헹궈 건져 놓고 콩나물은 다듬어 씻고 청양고추는 어슷썰기 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_3.png'),
    (6, 3, '3. 황태는 손질하여 물에 헹궈 건져 놓고 콩나물은 다듬어 씻고 청양고추는 어슷썰기 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_3.png'),
    (6, 4, '4. 냄비에 물을 붓고 황태와 무를 넣고 끓인 후 육수에서 물을 건져내고 저염 된장을 푼다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_4.png'),
    (6, 5, '5. 콩나물, 다진 마늘, 청양고추를 넣고 뚜껑을 덮어 김이 나게 끓여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_5.png'),
    (7, 1, '1. 감자, 양파는 얇게 썰고 애느타리 버섯은 썰어 달궈진 팬에 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00037_2.png'),
    (7, 2, '2. 냄비에 물을 붓고 된장을 푼 뒤 감자, 양파, 두부를 넣어 재료가 투명해지게 끓인 후 된장국의 재료를 건져서 믹서에 넣어 갈고 된장국에 넣어 한번 더 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00037_4.png'),
    (7, 3, '3. 구운 애느타리버섯과 대파를 국에 넣어 끓인 후 그릇에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00037_6.png'),
    (8, 1, '1. 찬물에 표고버섯,머리와 내장을 제거한 멸치, 다시마, 양파를 넣어 중간 불에서 10분 정도 끓여 멸치육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00038_1.png'),
    (8, 2, '2. 육수의 표고버섯 기둥, 멸치, 다시마, 양파를 건진 후 표고버섯은 편으로 썰고 청경채는 4cm 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00038_2.png'),
    (8, 3, '3. 끓는 육수에 준비한 표고버섯과 청경채를 넣고 국간장으로 간을 한 후 마늘을 넣어 한소끔 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00038_4.png'),
    (9, 1, '1. 올리브유, 식초, 설탕, 다진 마늘을 섞어 거품기로 충분히 저어주어 올리브마늘 드레싱을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00095_2.png'),
    (9, 2, '2. 치커리는 싱싱하게 찬물에 담갔다가 물기를 뺀 후 한입 크기로 자르고, 적양배추, 양파, 당근은 곱게채를 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00095_3.png'),
    (9, 3, '3. 접시에 준비한 치커리, 적양배추, 양파, 당근을 담고 올리브마늘 드레싱을 뿌린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00095_4.png'),
    (10, 1, '1. 찬물이 담긴 냄비에 식초, 소금을 넣고 메추리알을 삶는다. 물이 끓어오르면 5분 정도 더 삶아 찬물에 헹군 후 껍질을 벗기고 반으로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00087_1.png'),
    (10, 2, '2. 딸기를 흐르는 물에 가볍게 씻어 꼭지를 제거한 후 물기를 빼고 반으로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00087_2.png'),
    (10, 3, '3. 양상추는 찬물에 담갔다가 물기를 빼고 한입 크기로 찢은 후 접시에 양상추, 딸기, 블루베리, 메추리알을 담고 플레인요거트를 끼얹는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00087_4.png'),
    (11, 1, '1. 시금치는 끓는 소금물에 데쳐 찬물에 헹구어 물기를 짜고 우유를 넣고 블렌더에 곱게 갈아 체에 거른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00089_1.png'),
    (11, 2, '2. 껍질 벗긴 감자는 찜기에 넣어 20분 정도 삶고 꺼내서 으깨고, 아몬드는 잘게 다지며, 치커리는 곱게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00089_2.png'),
    (11, 3, '3. 으깬 감자, 다진 치커리, 시금치 우유소스, 설탕을 넣고 섞어 접시에 담고 아몬드, 크랜베리를 올리고 시금치우유 소스를 곁들인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00089_5.png'),
    (12, 1, '1. 곱게 다진 양파와 오이피클은 물기를 짠다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00091_1.png'),
    (12, 2, '2. 연두부에 다진 양파, 다진 오이피클, 올리브유, 머스터드, 후추, 꿀, 식초, 레몬즙을 넣어 두부타르타르 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00091_2.png'),
    (12, 3, '3. 새송이버섯은 0.5cm 두께로 썰고 프라이팬에 노릇하게 구워준 후 접시에 새송이버섯과 치커리를 담고 소스를 곁들인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00091_5.png'),
    (13, 1, '1. 저염간장, 식초, 레몬즙, 설탕을 혼합하여 간장레몬 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00093_1.png'),
    (13, 2, '2. 호박, 가지, 새송이버섯은 3cm 길이로 자른 후 얇은 편으로 채 썰고, 양파, 파프리카, 피망은 호박 길이로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00093_2.png'),
    (13, 3, '3. 가지, 호박, 새송이버섯, 양파, 파프리카, 피망에 올리브유를 바르고 달궈진 그릴 팬에 구운 후 접시에 담고 발사믹 크레타를 뿌리고 간장레몬 소스를 곁들인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00093_3.png'),
    (14, 1, '1. 두유에 플레인요거트와 레몬즙, 설탕을 넣고 잘 섞어 두유요거트 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00094_1.png'),
    (14, 2, '2. 강낭콩은 물에 불린 후 콩과 물을 1:2.5로 넣고 20~25분 삶아 건지고 브로콜리와 컬리플라워는 한입 크기로 썰어 끓는 소금물에 살짝 데쳐 찬물에 헹궈 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00094_4.png'),
    (14, 3, '3. 접시에 데친 브로콜리와 컬리플라워, 삶은 강낭콩, 채썬 적양파, 다진 호두, 건포도를 담고 두유요거트 소스를 곁들여 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00094_5.png'),
    (15, 1, '1. 오렌지는 깨끗이 씻어 껍질을 벗긴다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_1.jpg'),
    (15, 2, '2. 당근은 깨끗이 씻어 작은 토막으로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_2.jpg'),
    (15, 3, '3. 당근, 오렌지, 물(50ml)을 믹서에 곱게 간다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_3.jpg'),
    (15, 4, '4. 믹서에 갈아낸 주스는 고운 체로 거른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_4.jpg'),
    (15, 5, '5. 주스를 살얼음이 생길 만큼 시원하게 냉동한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_5.jpg'),
    (15, 6, '6. 컵에 담아 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00108_6.jpg'),
    (16, 1, '1. 잣, 아몬드, 해바라기씨, 호두, 호박씨는 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00111_1.png'),
    (16, 2, '2. 끓는 물에 저염소금과 식초를 넣은 후 소면을 삶은 후 체에 건져 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00111_2.png'),
    (16, 3, '3. 석류는 물 600g와 함께 믹서에 갈고 체에 걸러 즙을 낸다. 그릇에 면을 담고 차갑게 식힌 석류즙을 붓고 견과류, 채 썬 오이를 고명으로 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00111_3.png'),
    (17, 1, '1. 검은콩을 1시간 찬물에 담가 불린 후 삶아 익힌 후 불순물을 제거하고 흑임자, 생수, 삶은 콩, 호두를 믹서에 갈아 살엄음이 생기도록 냉동실에 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00113_1.png'),
    (17, 2, '2. 오이는 돌려 깎아 채 썰고, 토마토는 1/8크기로 썰고, 달걀은 삶아 반으로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00113_4.png'),
    (17, 3, '3. 실곤약은 끓는 물에 데치고 찬물에 헹궈 그릇에 담은 후 콩물을 담고 오이, 토마토, 달걀을 고명으로 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00113_6.png'),
    (18, 1, '1. 브로콜리, 숙주, 표고버섯, 닭 가슴살은 다지고 다진 닭 가슴살은 소금과 후추로 밑간 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00114_1.png'),
    (18, 2, '2. 다진 채소와 닭가슴살, 참기름, 저염간장을 섞어 만두소를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00114_3.png'),
    (18, 3, '3. 준비한 만두피에 만두소를 넣은 후 만두 모양으로 빚고 만두를 찜통에 넣어 찌고 그릇에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00114_4.png'),
    (19, 1, '1. 함초, 노루궁뎅이버섯, 다시마, 파, 양파, 무를 물에 넣어 10분 끓이고 향이 강한 모시조개와 냉이를 넣고 30분 끓인 후 간장으로 색을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00131_1.png'),
    (19, 2, '2. 소면은 따로 삶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00131_4.png'),
    (19, 3, '3. 그릇에 삶은 소면을 담은 후 육수를 붓고 지단, 슬라이스한 청고추, 실고추를 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00131_5.png'),
    (20, 1, '1. 두부에 쌀뜨물을 넣고 갈아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00137_1.png'),
    (20, 2, '2. 돼지고기는 썰고 김치는 물에 씻어 양념을 제거하고 3×3cm로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00137_2.png'),
    (20, 3, '3. 냄비에 돼지고기와 김치를 함께 볶은 후, 망에 거른 된장과 1의 두부, 고춧가루, 청양고추, 홍고추를 넣어 끓이다가 대파를 넣어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00137_3.png'),
    (21, 1, '1. 김치, 베이컨, 스팸, 소시지, 양파, 두부, 무는 두께 0.5cm로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00138_1.png'),
    (21, 2, '2. 다시마와 물을 끓여 다시마물을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00138_2.png'),
    (21, 3, '3. 냄비에 소시지, 베이컨, 두부, 스팸, 무, 우민찌, 김치, 다시마물 300g을 넣어 끓인 후 저염된장, 양파, 대파, 다진 마늘, 떡국 떡을 넣고 재료가 다 익으면 홍고추와 청양고추를 넣어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00138_3.png'),
    (22, 1, '1. 감자는 믹서에 갈고 양배추는 채 썰고 고기, 당근, 청양고추, 두부는 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00181_4.png'),
    (22, 2, '2. 준비해둔 재료를 모두 섞고 부침가루와 계란을 넣어 반죽한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00181_5.png'),
    (22, 3, '3. 가열된 팬에 기름을 두르고 반죽을 부어 굽고 소스를 함께 곁들인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00181_6.png'),
    (23, 1, '1. 청양고추와 홍고추를 다져서 참기름에 살짝 볶은 후 볶은 고추에 마요네즈와 레몬즙을 넣어 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00074_1.png'),
    (23, 2, '2. 돌나물은 손질하여 찬물에 담구고 새송이버섯은 달군 프라이팬에 참기름을 두르고 노릇하게 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00074_4.png'),
    (23, 3, '3. 돌나물은 물기를 빼고 구운 새송이버섯과 레몬마요네즈 소스를 넣고 버무려 접시에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00074_5.png'),
    (24, 1, '1. 배추를 소금물에 하루 동안 절인 후 물기를 빼고 석류는 즙을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00159_1.png'),
    (24, 2, '2. 무는 채 썰고, 쪽파와 미나리는 4cm로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00159_3.png'),
    (24, 3, '3. 무채, 미나리, 쪽파, 다진 마늘, 다진 생강, 새우젓국을 넣고 섞어 소를 만들고 절인 배추에 소를 넣고 오므려 싼다. 석류즙에 소를 채운 배추를 담가 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00159_4.png'),
    (25, 1, '1. 발사믹 식초, 화이트와인, 다진 유자청 건더기를 넣고 졸여 소스를 만들고 대구를 손질하여 포를 뜬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00161_1.png'),
    (25, 2, '2. 대파, 청파프리카, 홍파프리카, 황파프리카를 채 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00161_3.png'),
    (25, 3, '3. 대구포에 밀가루를 묻혀 청파프리카, 홍파프리카, 황파프리카, 황금팽이버섯을 넣고 말아 미나리로 묶어 찐 후 소스를 뿌리고 썬 대파를 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00161_4.png'),
    (26, 1, '1. 황파프리카를 갈아 체에 내리고 멥쌀로 쌀풀을 만들어 황파프리카 즙을 섞고 물엿을 첨가하여 양념을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00162_1.png'),
    (26, 2, '2. 배추를 소금물에 절이고 절인 배추를 찬물로 씻어 소금기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00162_3.png'),
    (26, 3, '3. 소금기를 뺀 배추에 채썬 자색고구마, 무, 쪽파, 부추, 사과, 배를 놓고 양념을 부은 후 3일 동안 숙성시켜 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00162_5.png'),
    (27, 1, '1. 양파와 마늘은 다져서 볶다가 토마토 과육과 설탕, 바질을 넣고 고추장을 섞어 토마토 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00193_1.png'),
    (27, 2, '2. 가지, 양송이버섯, 애호박, 사과는 썰고 살라미는 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00193_2.png'),
    (27, 3, '3. 오븐용 팬에 토마토 소스를 넓게 펴준 뒤 애호박, 가지, 양송이버섯, 살라미를 겹치게 넣고 180℃로 예열한 오븐에 넣고 30분 구워 완성한다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00193_3.png'),
    (28, 1, '1. 간장, 레몬즙, 설탕, 다진 홍고추를 섞어 드레싱을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00201_1.png'),
    (28, 2, '2. 민들레 잎은 4×4cm로 썰고, 비트는 채 썰고 물에 담가 색을 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00201_2.png'),
    (28, 3, '3. 오렌지는 씻어 껍질만 발라 채 썰고 민들레 잎, 비트, 오렌지 껍질을 섞어 접시에 담고 드레싱을 곁들인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00201_4.png'),
    (29, 1, '1. 올리브오일, 발사믹식초, 후춧가루를 섞어 차게 식혀 드레싱을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00202_1.png'),
    (29, 2, '2. 가지와 토마토는 5mm두께로 썰고 120도 오븐에 20분 구운 후 구운 가지는 물기를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00202_2.png'),
    (29, 3, '3. 팬에 올리브오일을 두르고 약불에서 채 썬 마늘을 볶다가, 가지를 넣고 구운 후 접시에 토마토, 가지를 번갈아 담고 무순으로 장식하고 드레싱을 곁들인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00202_5.png'),
    (30, 1, '1. 끓는 물에 배춧잎을 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_1.png'),
    (30, 2, '2. 당근, 호박, 표고버섯, 양파는 입자있게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_2.png'),
    (30, 3, '3. 돼지고기를 썰어 불린 쌀, 다진 야채와 함께 밥을 짓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_3.png'),
    (30, 4, '4. 데친 배춧잎에 지어놓은 밥을 올려 돌돌만다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_4.png'),
    (30, 5, '5. 말아 놓은 배춧잎을 부추로 묶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_5.png'),
    (30, 6, '6. 찜통에 김이 오르면 ?번을 넣고 약 5분 정도 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_6.png'),
    (31, 1, '1. 대추는 돌려 깍아 씨를 제거 하고 곱게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_1.png'),
    (31, 2, '2. 다진 소고기와 돼지고기에 대추와 소금 후춧가루를 넣고 치댄다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_2.png'),
    (31, 3, '3. 애호박과 단호박, 파프리카는 입자있게 썰어 ?에 넣고 잘 치댄다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_3.png'),
    (31, 4, '4. 양송이는 편으로 썰어 팬에 익히고, 인삼을 뇌두를 자르고 깨끗이 씻어 팬에 볶아 잘게 썰어 ?에 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_4.png'),
    (31, 5, '5. 재료가 골고루 섞인 떡갈비를 갈비 모양으로 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_5.png'),
    (31, 6, '6. 떡갈비를 팬에 굽고, 접시에 배춧잎을 깔고 익힌 양송이를 올리고 떡갈비를 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_6.png'),
    (32, 1, '1. 닭을 깨끗이 손질하고, 부위별로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_1.png'),
    (32, 2, '2. 냄비에 가시오가피, 대파, 다시마, 마늘, 건새우를 넣고 은근히 끓인 후 닭을 넣고 약 20분 정도 삶아 건진 뒤 살을 찢어둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_2.png'),
    (32, 3, '3. 양파, 오이는 채썰고 식초, 설탕, 소금을 넣고 초절임한다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_3.png'),
    (32, 4, '4. 겨자가루에 30℃ 정도의 물을 넣고 골고루 섞어 발효시키고, 설탕, 식초, 소금을 넣고 겨자소스를 만든다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_4.png'),
    (32, 5, '5. 흰곤약과 비트, 치자가루를 섞은 곤약, 채썬 다시마를 접시에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_5.png'),
    (32, 6, '6. 양파, 오이 초절임과 닭살을 한쪽에 담아 먹기 직전에 겨자소스를 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_6.png'),
    (33, 1, '1. 찹쌀은 깨끗이 씻어 30분 정도 불린다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_1.png'),
    (33, 2, '2. 불린 찹쌀은 냄비에 넣고 질게 밥을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_2.png'),
    (33, 3, '3. 닭가슴살은 넓게 펴서 소금, 후춧가루를 뿌린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_3.png'),
    (33, 4, '4. 호박잎과 미나리는 끓는 물에 살짝 데치고, 대추는 돌려 깎아 씨를 제거하고 인삼은 뇌두를 제거한 뒤 채를 썰어둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_4.png'),
    (33, 5, '5. 데친 호박잎에 닭 가슴살을 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_5.png'),
    (33, 6, '6. 닭 가슴살위에 찹쌀밥과 준비한 야채와 대추를 올려 돌돌 말아 질게 된 찹쌀밥에 넣고 약 20분 정도 더 쪄낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_6.png'),
    (34, 1, '1. 된장에 두부를 넣고 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_1.png'),
    (34, 2, '2. 표고버섯과 마늘은 함께 갈아둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_2.png'),
    (34, 3, '3. 미역은 충분히 불린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_3.png'),
    (34, 4, '4. 냄비에 황태채와 갈아둔 표고버섯, 마늘을 넣고 은근히 끓여 육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_4.png'),
    (34, 5, '5. 곤약은 납작납작하게 썰어 끓는 물에 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_5.png'),
    (34, 6, '6. 육수에서 황태를 건져 두부된장과 함께 갈아 육수에 붓고, 미역, 곤약, 들깨가루를 넣고 약 20분 정도 끓인 후 액젓으로 간을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_6.png'),
    (35, 1, '1. 훈제오리 가슴살을 슬라이스한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_1.png'),
    (35, 2, '2. 슬라이스한 훈제오리는 팬에 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_2.png'),
    (35, 3, '3. 양파는 채썰고 찬물에 담근 뒤 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_3.png'),
    (35, 4, '4. 레디쉬는 채썰고 찬물에 담근 뒤 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_4.png'),
    (35, 5, '5. 먹기 직전에 발사믹소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_5.png'),
    (35, 6, '6. 접시에 훈제오리와 양파채, 레디쉬, 블루베리를 담고 발사믹소스를 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_6.png'),
    (36, 1, '1. 훈제오리 가슴살은 올리브오일, 로즈마리, 후춧가루로 마리네이드한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_1.png'),
    (36, 2, '2. 새송이버섯은 편으로 썰고 팬에 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_2.png'),
    (36, 3, '3. 오렌지는 슬라이스로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_3.png'),
    (36, 4, '4. 땅콩과 파슬리는 입자있게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_4.png'),
    (36, 5, '5. 마리네이드한 오리 고기를 구워 접시에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_5.png'),
    (36, 6, '6. 유자청에 땅콩, 파슬리가루를 넣고 소스를 만들고, 구워진 오리고기 사이에 오렌지 슬라이스를 넣고 유자청소스를 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_6.png'),
    (37, 1, '1. 전복은 수저로 떼어내어 소금으로 비벼 깨끗이 씻는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_1.png'),
    (37, 2, '2. 시금치는 데쳐 다지고, 새우는 껍질을 벗겨 전복, 해초와 함께 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_2.png'),
    (37, 3, '3. ?번을 잘 섞어 전복 껍질에 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_3.png'),
    (37, 4, '4. 찜통에 김이 오르면 속을 채운 전복 껍질을 넣고 약 10분 정도 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_4.png'),
    (37, 5, '5. 저염간장, 레몬, 설탕, 다진마늘을 넣고 살짝 끓여 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_5.png'),
    (37, 6, '6. 간장소스에 전분을 풀어 농도를 맞추고, 전복찜에 여러번 바르고 식용꽃을 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_6.png'),
    (38, 1, '1. 검은콩은 깨끗이 씻어 약 12시간 정도 충분히 불린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_1.png'),
    (38, 2, '2. 불린 검은콩은 20분 정도 삶아 깨끗이 씻는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_2.png'),
    (38, 3, '3. 삶은 검은콩은 믹서기에 곱게 갈아 차갑게 식힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_3.png'),
    (38, 4, '4. 오이는 씨를 제거하고 깨끗이 씻어 채썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_4.png'),
    (38, 5, '5. 물을 끓여 메밀면을 삶고, 찬물에 여러번 헹구어 건져 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_5.png'),
    (38, 6, '6. 메밀면을 그릇에 담고 차가운 콩 국물, 땅콩, 잣, 오이, 방울토마토 및 통깨를 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_6.png'),
    (39, 1, '1. 어린잎은 찬물에 담근다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_1.png'),
    (39, 2, '2. 마는 껍질을 벗겨 동글동글하게 썬다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_2.png'),
    (39, 3, '3. 썰어 놓은 마는 팬에 기름을 살짝 두르고 앞뒤로 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_3.png'),
    (39, 4, '4. 참나물, 돈나물, 토마토, 레디쉬는 먹기 좋은 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_4.png'),
    (39, 5, '5. 겨자가루는 30℃ 물을 섞어 발효 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_5.png'),
    (39, 6, '6. 저염간장에 식초, 설탕, 발효겨자를 넣고 골고루 섞어 소스를 만들고, 접시에 구운 마와 손질한 야채를 넣고 만들어 놓은 소스와 어린잎을 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_6.png'),
    (40, 1, '1. 육수 재료를 넣고 중간 불로 끓인 뒤 체에 걸러 육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_1.png'),
    (40, 2, '2. 닭가슴살은 채 썬 뒤 물에 넣고 삶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_2.png'),
    (40, 3, '3. 애호박은 반달 썰고, 미나리는 5㎝ 길이로 썰고, 청양고추는 잘게 썬 뒤 찬물에 담가 씨를 빼고, 두부는 한입 크기로 썰어 물에 담가둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_3.png'),
    (40, 4, '4. 김치를 한입 크기로 썬 뒤 참기름에 볶다가 육수를 붓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_4.png'),
    (40, 5, '5. 두부, 애호박, 느타리버섯, 미나리, 닭가슴살을 넣어 끓어오르면 달걀물과 청양고추를 넣고 조금 더 끓여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_5.png'),
    (41, 1, '1. 대파, 마늘, 쇠고기를 물(300g)과 함께 끓인 뒤 면포에 걸러 육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_1.png'),
    (41, 2, '2. 콩나물, 김치를 물에 씻어 손질한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_2.png'),
    (41, 3, '3. 두부는 물기를 빼고, 붉은 고추는 다진 뒤 돼지고기와 섞어 반죽해 동그랗게 빚어 완자를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_3.png'),
    (41, 4, '4. 김 오른 찜기에 완자를 넣어 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_4.png'),
    (41, 5, '5. 육수를 끓이고 끓어오르면 김치, 콩나물, 새우젓을 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_5.png'),
    (41, 6, '6. 한소끔 더 끓이다가 완자를 넣고 조금 더 끓여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_6.png'),
    (42, 1, '1. 풋고추는 잘게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_1.png'),
    (42, 2, '2. 백김치는 먹기 좋은 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_2.png'),
    (42, 3, '3. 냄비에 백김치를 볶다가 김치가 익으면 참기름, 후춧가루, 다진 마늘을 넣고 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_3.png'),
    (42, 4, '4. 물(200g)을 넣고 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_4.png'),
    (42, 5, '5. 물이 끓으면 콩비지와 간장, 풋고추를 넣어 약한 불에서 15분 정도 끓여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_5.png'),
    (43, 1, '1. 육수 재료를 넣고 끓인 뒤 체로 걸러 육수를 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_1.png'),
    (43, 2, '2. 오징어 몸통은 큼지막하게 썰고 다리는 얇게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_2.png'),
    (43, 3, '3. 두부와 버섯은 먹기 좋게 썰고, 고추는 어슷하게 썰고, 고구마는 먹기 좋은 크기로 썰고, 콩나물은 다듬는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_3.png'),
    (43, 4, '4. 김치는 먹기 좋은 크기로 썬 뒤 냄비에 참기름을 두르고 볶다가 고구마, 콩나물, 육수를 넣고 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_4.png'),
    (43, 5, '5. 자작하게 끓으면 오징어를 넣고 끓이고, 오징어가 익으면 들깻가루를 넣고 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_5.png'),
    (43, 6, '6. 두부, 팽이버섯, 쑥갓, 고구마, 붉은 고추를 넣고 끓여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_6.png'),
    (44, 1, '1. 냄비에 육수 재료를 넣고 끓이다가 물이 끓어오르면 다시마를 건지고 조금 더 끓여 육수를 우려낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306022942_1678080582607.jpg'),
    (44, 2, '2. 주꾸미는 내장과 입, 눈을 제거하고 4cm 크기로 잘라 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023039_1678080639676.jpg'),
    (44, 3, '3. 김치는 국물을 꽉 짜서 한입 크기로 썰고, 무는 편 썰고, 마늘은 다지고, 대파는 어슷 썰고, 양파는 굵게 채 썰고, 두부는 납작하게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023103_1678080663820.jpg'),
    (44, 4, '4. 냄비에 참기름을 두르고, 김치, 무, 양파가 투명해질 때까지 볶다가 육수를 붓고 끓으면 주꾸미, 다진 마늘을 넣어 더 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230313/20230313110637_1678673197807.jpg'),
    (44, 5, '5. 김칫국물과 배즙을 2:1로 섞은 뒤 찌개에 넣어 간을 맞춘다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023149_1678080709863.jpg'),
    (44, 6, '6. 두부, 팽이버섯을 넣고 한소끔 끓여 마무리한다', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023210_1678080730863.jpg'),
    (45, 1, '1. 다시마육수재료를 물에 넣고 끓여 육수를 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_1.png'),
    (45, 2, '2. 느타리버섯은 가닥가닥 떼고, 두부는 썰고, 콩나물은 머리와 꼬리를 떼 손질한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_2.png'),
    (45, 3, '3. 배추와 미나리를 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_3.png'),
    (45, 4, '4. 돼지고기, 쇠고기에 고기완자양념을 넣어 반죽한 뒤 빚어 완자를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_4.png'),
    (45, 5, '5. 냄비에 모든 재료를 돌려 담은 뒤 육수를 부어 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_5.png'),
    (46, 1, '1. 통조림 햄은 한 번 데친 뒤 우유에 담가두고, 실곤약은 식초를 넣은 끓는 물에 살짝 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_1.png'),
    (46, 2, '2. 양파는 굵게 채 썰고, 느타리버섯은 찢고, 표고버섯은 모양대로 썰고, 팽이버섯은 끝부분을 잘라내고, 청양고추와 붉은 고추는 어슷 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_2.png'),
    (46, 3, '3. 감자, 무, 두부는 0.5㎝ 두께로 썰고, 애호박은 반달썰기 하고 깻잎은 채 썰어 찬물에 담가 매운맛을 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_3.png'),
    (46, 4, '4. 사골육수에 다시마를 넣어 끓인 후 한 김 식힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_4.png'),
    (46, 5, '5. 다진 돼지고기에 다진 파, 다진 마늘, 후춧가루, 소금을 섞어 완자를 만들고 밀가루와 달걀물을 묻혀 식용유(5g)를 두른 팬에 부친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_5.png'),
    (46, 6, '6. 냄비에 재료를 보기 좋게 담고 사골육수를 부은 뒤 끓이다가 쑥갓과 들깻가루를 넣어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_6.png'),
    (47, 1, '1. 육수 재료를 넣고 20분간 끓여 육수를 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_1.png'),
    (47, 2, '2. 닭가슴살과 당근을 다져 모양을 잡은 뒤 김 오른 찜기에 넣고 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_2.png'),
    (47, 3, '3. 고추, 대파는 어슷 썰고, 쑥갓, 표고버섯, 애호박, 김치는 먹기 좋은 크기로 썰고, 팽이버섯은 밑동을 잘라 손질한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_3.png'),
    (47, 4, '4. 당면과 숙주는 뜨거운 물에 데쳐 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_4.png'),
    (47, 5, '5. 양념장 재료를 섞은 뒤 육수(13g)를 넣고 양념장을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_5.png'),
    (47, 6, '6. 준비한 재료를 담고 육수를 부은 뒤 양념장을 넣고 끓여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_6.png'),
    (48, 1, '1. 통조림 햄을 썰어 끓는 물에 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012641_1678249601913.jpg'),
    (48, 2, '2. 애호박은 반달모양으로 썰고, 두부는 도톰하게 썰고, 팽이버섯은 3등분하고, 청양고추는 어슷 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012702_1678249622285.jpg'),
    (48, 3, '3. 김치는 씻어서 잘게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012717_1678249637881.jpg'),
    (48, 4, '4. 양념장을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012732_1678249652118.jpg'),
    (48, 5, '5. 냄비에 물과 다시마를 넣고 살짝 끓여 육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012747_1678249667092.jpg'),
    (48, 6, '6. 육수에 손질한 재료를 담고 양념장의 1/2 분량을 넣고 중간 불에 끓이다가 쑥갓과 남은 양념장을 넣고 약한 불에 조금 더 끓여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012805_1678249685798.jpg'),
    (49, 1, '1. 굴은 깨끗이 씻은 뒤 김 오른 찜기에 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_1.png'),
    (49, 2, '2. 굴을 쪄낸 물(250g)에 멸치, 표고버섯 밑동을 넣고 끓인 뒤 멸치와 표고버섯 밑동을 건져내 육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_2.png'),
    (49, 3, '3. 느타리버섯은 밑동을 제거한 후 씻어 세로로 찢고, 표고버섯은 0.5㎝ 두께로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_3.png'),
    (49, 4, '4. 무는 나박 썰고 양파는 채 썰고, 미나리는 3㎝ 길이로 썰고, 대파와 고추는 송송 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_4.png'),
    (49, 5, '5. 육수에 굴, 다진 마늘, 무, 양파, 버섯을 넣고 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_5.png'),
    (49, 6, '6. 들깻가루와 순두부를 넣고 한소끔 끓인 뒤 국간장과 소금으로 간을 맞추고 대파, 붉은 고추, 미나리를 올려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_6.png'),
    (50, 1, '1. 냄비에 육수 재료를 넣어 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_1.png'),
    (50, 2, '2. 콩을 충분히 불려 믹서기에 물을 넣고 갈아 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_2.png'),
    (50, 3, '3. 느타리버섯은 찢고, 표고버섯은 먹기 좋은 크기로 자르고, 애호박과 양파는 깍둑 썰고, 대파와 고추는 송송 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_3.png'),
    (50, 4, '4. 바지락은 해감해 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_4.png'),
    (50, 5, '5. 육수에 콩, 느타리버섯, 표고버섯, 애호박, 저염된장, 새우, 바지락을 넣고 끊인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_5.png'),
    (50, 6, '6. 순두부를 넣고 끊인 후 양파, 대파, 붉은 고추, 풋고추를 넣고 한소끔 끓여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_6.png'),
    (51, 1, '1. 표고버섯은 머리와 밑동을 분리하고, 머리 부분은 잘게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053656_1678264616396.jpg'),
    (51, 2, '2. 애호박, 감자, 양파는 한입 크기로 썰고, 미나리와 부추는 3cm 길이로 썰고, 대파와 청양고추는 송송 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053712_1678264632312.jpg'),
    (51, 3, '3. 물에 다시마, 표고버섯 밑동을 넣고 끓으면 건더기를 건져 육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053727_1678264647414.jpg'),
    (51, 4, '4. 육수에 저염된장과 찹쌀가루를 풀어 한소끔 끓이고, 감자와 다진마늘을 넣어 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053743_1678264663037.jpg'),
    (51, 5, '5. 애호박과 표고버섯을 넣고, 순두부, 양파, 들깻가루, 저염국간장을 넣어 한소끔 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053800_1678264680719.jpg'),
    (51, 6, '6. 대파, 청양고추, 미나리, 부추를 듬뿍 올려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053819_1678264699729.jpg'),
    (52, 1, '1. 토란은 2×2㎝로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_1.png'),
    (52, 2, '2. 표고버섯은 채 썰고, 고추는 어슷하게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_2.png'),
    (52, 3, '3. 냄비에 물(500ml)을 넣고 물이 끓으면 백합을 넣고 끓여 육수를 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_3.png'),
    (52, 4, '4. 조개가 입을 벌리면 토란과 들깻가루, 표고버섯을 넣고 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_4.png'),
    (52, 5, '5. 육수가 어느 정도 걸쭉해지면 순두부를 넣고 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_5.png'),
    (52, 6, '6. 붉은 고추와 청양고추를 올려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_6.png'),
    (53, 1, '1. 토마토홀과 토마토는 송송 다져주고, 마늘, 양파를 냄비에서 볶은 후 믹서에 갈아 토마토 스프를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_01.png'),
    (53, 2, '2. 만들어진 스프를 볶아서 신맛을 날려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_02.png'),
    (53, 3, '3. 먹물파스타면은 삶은 뒤 오일에 버무려주고 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_03.png'),
    (53, 4, '4. 양파, 애호박, 가지, 2가지색 파프리카를 먹기 좋게 잘라 팬에 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_04.png'),
    (53, 5, '5. 접시에 볶아진 채소와 파스타면을 서로 다시 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_05.png'),
    (53, 6, '6. 치즈를 곱게 다져주고 접시에 파스타면을 올린 뒤, 토마토스프와 치즈가루를 뿌려 완성 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_06.png'),
    (54, 1, '1. 양파, 당근, 오이는 채 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_01.png'),
    (54, 2, '2. 열무 물김치와 탄산수, 어간장을 넣어 국수 국물을 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_02.png'),
    (54, 3, '3. 달걀은 삶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_03.png'),
    (54, 4, '4. 실곤약은 삶을 때 레몬즙을 넣어서 곤약의 특이한 냄새를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_04.png'),
    (54, 5, '5. 물기를 제거한 곤약에 어간장을 넣어 밑간을 해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_05.png'),
    (54, 6, '6. 그릇에 담고 국물을 부어주고, 채소와 달걀을 곁들여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_06.png'),
    (55, 1, '1. 오징어는 솔방울 모양으로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_01.png'),
    (55, 2, '2. 미니 파프리카, 양파, 청홍고추는 적당한 크기로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_02.png'),
    (55, 3, '3. 다진마늘을 볶다가 오징어를 넣어 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_03.png'),
    (55, 4, '4. 홀토마토와 양념장 재료를 넣어 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_04.png'),
    (55, 5, '5. 팬을 약간 태우는 듯 해서 불맛과 매운맛을 더해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_05.png'),
    (55, 6, '6. 접시에 담아주고 볶은 현미를 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_06.png'),
    (56, 1, '1. 뚜껑을 만들 배의 윗동을 자르고, 수저로 배 속을 판다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00388_01.png'),
    (56, 2, '2. 탄산수와 생강청을 미리 30분 전에 섞어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00388_02.png'),
    (56, 3, '3. 방울토마토를 먹기 좋게 잘라준 후 속을 파낸 배에 후르츠 칵테일과 방울토마토를 넣어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00388_03.png'),
    (56, 4, '4. 과일로 채워진 배에 미리 만든 생강탄산수를 부어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00388_04.png'),
    (56, 5, '5. 배를 뚜껑을 덮어 호일로 싼 후 팬에 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00388_05.png'),
    (57, 1, '1. 쇠고기는 칼집을 살짝 넣어준 후 건 바질을 뿌려 1시간 정도 숙성시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_01.png'),
    (57, 2, '2. 감자는 껍질을 벗긴 후 먹기 좋게 잘라 뜨거운 물에 70% 정도 익혀준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_02.png'),
    (57, 3, '3. 익혀준 감자를 버터를 살짝 바른 후 팬에 파슬리가루를 뿌려 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_03.png'),
    (57, 4, '4. 시금치는 데쳐내 물기를 제거하고 다진 양파와 함께 버터에 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_04.png'),
    (57, 5, '5. 사과는 사방 1cm 크기로 자른 후 레몬즙을 살짝 뿌려준 후 버터에 볶아 소스를 만들어 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_05.png'),
    (57, 6, '6. 숙성된 소고기를 팬에 구워주고 곁들이 채소와 소스를 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_06.png'),
    (58, 1, '1. 오징어는 내장을 빼서 흐르는 물에 깨끗이 씻은 후, 다리는 송송 다져주고 레몬즙을 살짝 뿌려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_01.png'),
    (58, 2, '2. 강낭콩은 먼저 삶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_02.png'),
    (58, 3, '3. 피망, 당근, 양파, 대파, 파프리카는 송송 다져준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_03.png'),
    (58, 4, '4. 잘라준 오징어다리는 다진마늘과 살짝 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_04.png'),
    (58, 5, '5. 볼에 귀리밥을 넣고 카레가루를 넣고 모든 재료를 섞어 소 재료를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_05.png'),
    (58, 6, '6. 오징어 안쪽에 밀가루를 묻혀주고 소를 채워 팬에 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_06.png'),
    (59, 1, '1. 소고기는 사방 2cm 정도의 크기로 잘라서 물에서 80% 정도까지 익혀준 후 냉수에 깨끗하게 세척해 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_01.png'),
    (59, 2, '2. 양파는 먹기 좋게 자르고 물 400g과 함께 간장 물을 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_02.png'),
    (59, 3, '3. 간장 물에 데쳐 놓은 소고기부터 졸여주기 시작한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_03.png'),
    (59, 4, '4. 메추리알을 넣고 함께 졸여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_04.png'),
    (59, 5, '5. 방울토마토, 양파, 마늘, 꽈리고추까지 넣고 마지막으로 졸여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_05.png'),
    (60, 1, '1. 당근, 양파, 샐러리는 사방 2cm 정도로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_01.png'),
    (60, 2, '2. 레몬은 껍질을 아주 곱게 채 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_02.png'),
    (60, 3, '3. 소고기는 사방 3cm 정도로 자르고 흰 후추를 뿌려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_03.png'),
    (60, 4, '4. 홀토마토는 먹기 좋게 부수어 준 후 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_04.png'),
    (60, 5, '5. 팬에 버터를 넣고 소고기부터 볶기 시작하다 채소를 넣고 볶아준다. 이 때 두꺼운 채소부터 볶아주는 것이 고기랑 익는 시간이 같아진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_05.png'),
    (60, 6, '6. 마지막에 다져 놓은 홀토마토를 넣고 함초소금 으로 간을 한 후 레몬껍질을 올려서 마무리 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_06.png'),
    (61, 1, '1. 소스 재료를 이용해 맛 간장으로 만든 데리 야끼소스를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_01.png'),
    (61, 2, '2. 참치는 손질해 수분을 빼준 후 흰 후추로 밑간을 해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_02.png'),
    (61, 3, '3. 생강은 아주 곱게 채 썰어 냉수에 헹군다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_03.png'),
    (61, 4, '4. 대파도 아주 가늘게 채 썰어서 냉수에 담가 매운맛을 빼준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_04.png'),
    (61, 5, '5. 버터를 올린 팬에 삼치를 약불에서 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_05.png'),
    (61, 6, '6. 구워진 참치를 소스에 졸여주고 대파채를 올린다. 삼치에 소스를 뿌리고 생강채를 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_06.png'),
    (62, 1, '1. 삼겹살은 길이로 잘라서 마늘기름, 통후추, 로즈마리에 마리네이드 해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_01.png'),
    (62, 2, '2. 대파는 흰 부분만 잘라 구워주고, 꽈리고추도 함께 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_02.png'),
    (62, 3, '3. 대파는 가늘게 채 썰어 물에 담가준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_03.png'),
    (62, 4, '4. 물에 담가진 대파를 물기를 빼고 양념해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_04.png'),
    (62, 5, '5. 삼겹살을 꼬치에 끼워 굽고 구워진 채소, 대파채와 함께 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_05.png'),
    (63, 1, '1. 저염 된장을 물 300cc에 풀어준 뒤 건새우를 담가 30분 정도 담가준 뒤 살짝 끓여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_01.png'),
    (63, 2, '2. 새우를 풀어준 된장 물을 체에 걸러준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_02.png'),
    (63, 3, '3. 아욱은 뜨거운 물에 데쳐서 먹기 좋게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_03.png'),
    (63, 4, '4. 냄비에 밥과 된장 물을 풀어 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_04.png'),
    (63, 5, '5. 어느 정도 끓으면 칵테일새우와 건져 놓았던 새우를 다시 넣어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_05.png'),
    (63, 6, '6. 불을 끄기 전에 아욱을 넣어 한 번 더 끓여준 후 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_06.png'),
    (64, 1, '1. 감자를 슬라이스 하여 찜통에 찐 후 체에 으깬다. 깻잎을 잘게 채 썰어서 냉수에 담가 향을 빼준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_01.png'),
    (64, 2, '2. 베이컨을 한 번 데쳐내 송송 썰어준 후 다진 마늘과 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_02.png'),
    (64, 3, '3. 으깬 감자와 깻잎을 다져서 반죽을 만들고 엄지손가락 크기로 떼어 밀가루를 뿌리고, 둥글게 빚은 후 포크로 자국을 내 뇨끼를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_03.png'),
    (64, 4, '4. 만들어진 뇨끼를 삶아 건져준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_04.png'),
    (64, 5, '5. 팬에 버터를 넣고 다진양파, 다진마늘, 다진 대파, 볶은 베이컨, 새송이, 흰후추를 볶다가 우유와 생크림, 설탕을 넣고 끓여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_05.png'),
    (64, 6, '6. 뇨끼를 넣어 한 번 더 끓여준 뒤 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_06.png'),
    (65, 1, '1. 단호박은 껍질을 까고 작게 잘라주고, 양파도 작게 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_01.png'),
    (65, 2, '2. 브로콜리는 뜨거운 물에 데쳐내 준 후 송송 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_02.png'),
    (65, 3, '3. 찹쌀가루는 물 100g에 풀어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_03.png'),
    (65, 4, '4. 단호박과 양파는 물 300g에 삶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_04.png'),
    (65, 5, '5. 단호박과 양파가 익으면 믹서에 갈아서 다시 끓여준 뒤 찹쌀가루물을 넣어 끓여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_05.png'),
    (65, 6, '6. 호박죽이 끓어오르면 치즈가루를 넣어준 후 볶은 현미랑 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_06.png'),
    (66, 1, '1. 팽이버섯을 송송 다진 후 라이스버거 재료로 라이스버거를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_01.png'),
    (66, 2, '2. 만들어준 버거는 팬에 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_02.png'),
    (66, 3, '3. 토마토와 미나리를 먹기 좋게 잘라서 겉절이를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_03.png'),
    (66, 4, '4. 떡갈비 재료로 떡 갈비를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_04.png'),
    (66, 5, '5. 만들어진 떡갈비를 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_05.png'),
    (66, 6, '6. 접시에 토마토 겉절이를 담고 라이스버거와 떡갈비를 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_06.png'),
    (67, 1, '1. 오이고추는 배를 갈라 씨를 제거하고 세척 후 절임물에 절여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052119_1678090879888.jpg'),
    (67, 2, '2. 영양부추, 연근은 송송 다지고 무는 채 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052136_1678090896972.jpg'),
    (67, 3, '3. 손질한 부추와 연근, 무에 절임물을 조금 넣고 절인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052154_1678090914303.jpg'),
    (67, 4, '4. 고춧가루, 생강청, 통깨를 절임채소에 섞어 소를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052208_1678090928991.jpg'),
    (67, 5, '5. 오이고추 속에 소를 채워 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052224_1678090944885.jpg'),
    (67, 6, '6. 오이고추를 그릇에 담고 남은 물을 부어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052240_1678090960385.jpg'),
    (68, 1, '1. 닭은 포를 떠서 생강즙과 정종을 섞어 발라 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_1.png'),
    (68, 2, '2. 시금치는 살짝 데쳐 썰고, 당근, 양파, 샐러리, 파프리카는 약 6Cm 길이로 채를 썰어 절반은 닭고기말이에 사용 하고, 절반은 곁들임채소로 사용한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_2.png'),
    (68, 3, '3. 느타리는 끓는 물에 소금을 넣고 데쳐 손으로 찢어놓고, 포를 뜬 닭가슴살에 느타리와 썰어 놓은 채소를 넣어 말아 주고, 육수를 끓여서 말아놓은 닭을 넣고 익혀낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_3.png'),
    (68, 4, '4. 주꾸미와 새우는 끓는 물에 살짝 데치고, 청경채는 반으로 썰어 살짝 데쳐 준비 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_4.png'),
    (68, 5, '5. 튀김기름 온도가 180℃ 정도 되면 누룽지를 넣고 튀겨 기름을 밭쳐 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_5.png'),
    (68, 6, '6. 닭을 넣은 육수(③)에 간장과 녹말, 마늘, 소금, 후춧가루를 넣고 끓이다가, 곁들임채소로 남겨 놓은 채소(②)와 데쳐 놓은 해물 및 청경채를 넣고 한 번 더 끓인 뒤 튀겨진 누룽지를 넣어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_6.png'),
    (69, 1, '1. 광어는 포를 길게 떠서 소금과 후춧가루에 밑간을 해 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_1.png'),
    (69, 2, '2. 아몬드와 호두는 입자있게 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_2.png'),
    (69, 3, '3. 밑간이 되어 있는 광어포에 밀가루와 달걀물을 입힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_3.png'),
    (69, 4, '4. 달걀물을 입힌 광어살에 다시 다져 놓은 견과류를 입혀 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_4.png'),
    (69, 5, '5. 팬에 올리브 오일을 넣고, 팬이 따끈해 지면 광어 살을 조심스럽게 앞뒤로 익혀 접시에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_5.png'),
    (69, 6, '6. 플레인요거트에 꿀과 레몬즙을 넣고 골고루 섞어 광어스테이크소스를 만들어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_6.png'),
    (70, 1, '1. 감자는 껍질을 벗겨 삶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00502_1.png'),
    (70, 2, '2. 오이와 당근은 어슷썰기를 하고 양파는 채를 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00502_2.png'),
    (70, 3, '3. 썰어 놓은 채소에 소금과 물을 넣고 살짝 절여, 물기를 짜놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00502_3.png'),
    (70, 4, '4. 삶아 놓은 감자를 채에 내리고 생크림을 넣어 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00502_4.png'),
    (70, 5, '5. 생크림을 넣은 감자에 절여 놓은 채소와 모짜렐라치즈를 넣고 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00502_5.png'),
    (70, 6, '6. 섞여진 감자를 크로켓 모양을 만들어 밀가루, 달걀물, 빵가루를 묻혀 튀김기름 170~180℃ 온도에서 바삭하게 튀겨 냅킨에 올려 기름을 제거하고, 접시에 담아 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00502_6.png'),
    (71, 1, '1. 두부는 소창에 넣고, 짜서 물기를 제거하고, 으깨어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_1.png'),
    (71, 2, '2. 으깬 두부에 소금과 코코넛 밀크를 넣고 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_2.png'),
    (71, 3, '3. 섞여진 두부를 김이 오른 찜통에 넣고 약 5분 정도 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_3.png'),
    (71, 4, '4. 당근과 양파는 사각지게 썰고, 브로컬리는 데쳐 당근 크기로 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_4.png'),
    (71, 5, '5. 돼지고기와 감자는 당근 크기로 썰어 주고, 냄비에 버터를 녹여 돼지고기를 먼저 볶다가 양파와 당근, 감자, 브로컬리를 넣고 다시 볶아 준 뒤, 쪄놓은 두부를 넣어 한 번 더 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_5.png'),
    (71, 6, '6. 볶아지는 냄비(⑤)에 카레 가루를 넣고 살짝 더 볶아준 뒤, 물을 넣고 자작하게 끓여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_6.png'),
    (72, 1, '1. 쌀은 불려 놓고, 마늘은 작게 다지고, 양파는 새송이와 파프리카와 함께 입자를 크게 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_1.png'),
    (72, 2, '2. 소고기는 포를 넓게 떠서 소금과 후춧가루를 뿌려 밑간을 해 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_2.png'),
    (72, 3, '3. 냄비에 버터를 넣고, 마늘과 양파를 먼저 볶다가, 새송이와 파프리카를 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_3.png'),
    (72, 4, '4. ③번에 다시 불린 쌀과 강화를 넣고, 볶다가 불을 줄여, 쌀이 익을 때까지 익혀 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_4.png'),
    (72, 5, '5. 쌀이 익으면, 밑간이 되어 있는 소고기에 넣고 돌돌 말아 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_5.png'),
    (72, 6, '6. 하얀 된장에 생크림을 넣어 골고루 섞고, 냄비에 육수를 넣고 생크림을 섞은 하얀 된장을 넣어 끓으면, 말아놓은 소고기를 넣고 은근히 졸여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_6.png'),
    (73, 1, '1. 닭고기살은 작게 썰어 소금과 후춧 가루를 살짝 뿌려 밑간을 하고 달걀을 넣어 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_1.png'),
    (73, 2, '2. 달걀을 넣어 섞은 닭고기 살에 녹말 가루를 넣어 골고루 묻힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_2.png'),
    (73, 3, '3. 국수는 각각 끓는 물에 살짝 데쳐 물기를 빼놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_3.png'),
    (73, 4, '4. 물기가 빠진 국수는 튀김기름 170℃ 온도에서 각각 작은 채에 그대로 살짝 튀겨 둥지를 만든 후 기름을 냅킨에 빼 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_4.png'),
    (73, 5, '5. 녹말가루를 묻힌 닭고기살도 튀김 기름에 튀겨 기름을 빼 놓은 둥지국수 위에 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_5.png'),
    (73, 6, '6. 냄비에 레몬과 설탕을 넣고 물을 넣어 끓이다가 레몬은 건져 내고, 마늘과 생강, 연유, 식초, 녹말을 풀어 넣고 살짝 한 번 더 끓여 녹말소스를 만들어, 튀겨진 닭고기 위에 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_6.png'),
    (74, 1, '1. 새우는 꼬리를 자르고 내장을 제거 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_1.png'),
    (74, 2, '2. 스테이크 소고기는 소금과 로즈마리를 뿌리고 후춧가루를 살짝 뿌려 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_2.png'),
    (74, 3, '3. 아스파라거스는 팬에 기름 없이 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_3.png'),
    (74, 4, '4. 새송이와 가지는 길게 썰어 브로컬리와 함께 기름 없이 팬에 살짝 구워 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_4.png'),
    (74, 5, '5. 팬에 버터를 살짝 넣고 새우를 굽고, 스테이크를 구워 접시에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_5.png'),
    (74, 6, '6. 팬에 와인과 설탕을 넣고 끓이다가, 당근, 양파와 토마토 마늘, 대파를 다져 넣고 볶듯이 끓이다가 발사믹소스를 넣고 한 번 더 졸인 소스를 스테이크 위에 얹고, 브로컬리와 새송이, 가지, 아스파라거스를 함께 놓아 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_6.png'),
    (75, 1, '1. 통 삼겹살에 함초와 소금, 후춧가루를 뿌려 숙성을 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_1.png'),
    (75, 2, '2. 부추는 약 6cm 길이로 썰고, 양파는 채를 썰어 놓고, 당근은 납작하게 편으로 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_2.png'),
    (75, 3, '3. 연두부는 잘게 다져 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_3.png'),
    (75, 4, '4. 유자청에 쌈장과 설탕, 생크림을 넣어 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_4.png'),
    (75, 5, '5. 골고루 섞은 유자청에 다져 놓은 연두부를 넣어 섞어 냄비에 넣고, 썰어 놓은 양파와 당근을 넣어 은근히 끓여 소스를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_5.png'),
    (75, 6, '6. 숙성시킨 통삼겹은 버터를 녹인 팬에 살짝 구워 다시 오븐에서 20분 정도 익힌 후 먹기 좋게 썰은 뒤, 접시에 부추를 깔고 그 위에 구워진 삼겹을 올리고 소스를 얹어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_6.png'),
    (76, 1, '1. 돼지고기와 소고기를 섞어, 매실액을 넣고 30분 정도 냉장고에서 숙성을 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_1.png'),
    (76, 2, '2. 숙성된 돼지고기와 소고기에 다진마늘, 로즈마리, 빵가루, 후춧가루를 넣고 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_2.png'),
    (76, 3, '3. 파프리카는 반으로 잘라 그 안에 씨를 수저를 이용해서 파낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_3.png'),
    (76, 4, '4. 속을 파낸 파프리카에 양념한 고기를 넣고, 꼭꼭 눌러 두툼하게 잘라 구워 접시에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_4.png'),
    (76, 5, '5. 양파는 입자있게 다져 팬에 기름을 넣고 은근히 볶아 양파 향을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_5.png'),
    (76, 6, '6. 볶아진 팬(⑤)에 준비한 플레인요거트와 설탕, 매실액을 넣고 끓이다가, 식초와 녹말을 넣고 한 번 더 끓여 소스를 완성한다. 햄버거스테이트(④)에 소스를 얹고, 어린잎을 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_6.png'),
    (77, 1, '1. 삼치는 반으로 잘라지지 않게 편으로 떠서 소금을 살짝 뿌려 잠시 말려 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_1.png'),
    (77, 2, '2. 손질을 한 삼치에 녹말가루를 입혀 팬에 기름을 두르고 구워 접시에 담아 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_2.png'),
    (77, 3, '3. 생강은 잘게 썰어, 물에 섞은 후 거름종이에 걸러 생강즙을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_3.png'),
    (77, 4, '4. 유자청에 만들어 놓은 생강즙을 넣고 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_4.png'),
    (77, 5, '5. 냄비에 레몬을 넣고, 물을 넣고 은근히 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_5.png'),
    (77, 6, '6. 레몬을 끓인 물에 ④번을 넣고 졸여 소스를 만든 후 접시에 담아 놓은 삼치 위에 소스로 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_6.png'),
    (78, 1, '1. 마늘은 곱게 다지고 양파, 당근, 방울토마토는 입자있게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_1.png'),
    (78, 2, '2. 토마토는 씨와 껍질을 제거하고 채를 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_2.png'),
    (78, 3, '3. 브로컬리를 끓는 물에 데쳐 찬물에 헹구어 작게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_3.png'),
    (78, 4, '4. 펜네는 끓는 물에 약 10분 정도 삶아 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_4.png'),
    (78, 5, '5. 건져진 펜네는 올리브오일을 넣어 무친 후 그릇에 담아놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_5.png'),
    (78, 6, '6. 냄비에 오일을 넣고 마늘과 양파를 볶다가 당근과 방울토마토를 넣어 볶으면서 육수를 붓고 설탕과 소금, 레몬즙, 월계수잎을 넣고 끓인 뒤 월계수잎을 건져내고, 차갑게 식힌 후 그릇에 담겨 있는 펜네에 토마토채(②)와 브로컬리(③)를 함께 넣어 담아 완성 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_6.png'),
    (79, 1, '1. 닭고기살은 끓는 물에 데쳐 손으로 찢고, 느타리버섯도 데쳐 손으로 찢어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_1.png'),
    (79, 2, '2. 사과는 씨를 제거하고 약 6cm 길이로 채를 썰고, 오이와 파프리카도 같은 길이로 채를 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_2.png'),
    (79, 3, '3. 애호박은 반달로 썰어 식초에 살짝 절여서 볶고, 양파는 채를 썰어 물에 잠시 담가 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_3.png'),
    (79, 4, '4. 아몬드는 우유를 넣어 갈아 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_4.png'),
    (79, 5, '5. 머스터드에 올리고당을 넣어 잘 섞어 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_5.png'),
    (79, 6, '6. 또띠아를 팬에 기름 없이 굽고, 그 안에 닭고기 살과 느타리버섯, 오이, 사과, 파프리카와 호박을 넣어 속이 보이도록 말아놓고, ④번과 ⑤번은 섞어서 소스를 만들어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_6.png'),
    (80, 1, '1. 닭고기살은 소금과 후춧가루, 생강즙과 정종을 뿌린 후 우유를 살짝 발라 숙성을 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_1.png'),
    (80, 2, '2. 오이는 길게 껍질째 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_2.png'),
    (80, 3, '3. 토마토는 반으로 잘라 편으로 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_3.png'),
    (80, 4, '4. 두부는 소창으로 물기를 꼭 짜 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_4.png'),
    (80, 5, '5. 물기를 제거 시킨 두부에 흑임자를 넣고 골고루 섞은 뒤 냄비에 넣고 우유와 함께 은근히 끓여 속을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_5.png'),
    (80, 6, '6. 팬에 토마토를 먼저 구워 접시에 담고, 숙성된 닭고기를 구워 올린 뒤, 그 위에 오이로 원형기둥을 만들고 구운 닭고기를 다시 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_6.png'),
    (81, 1, '1. 통오징어는 다리를 떼어내고 내장을 제거시켜 통째로 깨끗이 씻어 준비 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_1.png'),
    (81, 2, '2. 양파와 당근, 부추, 생강은 입자있게 썰고, 견과류는 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_2.png'),
    (81, 3, '3. 두부는 물기를 짠 후 수저로 으깨어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_3.png'),
    (81, 4, '4. 잘게 썰은 오징어 다리와 썰어 놓은 채소, 견과류(②), 곱게 다진 대파와 마늘, 그리고 소금과 녹말가루를 두부 (③)와 함께 골고루 섞어 순대속을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_4.png'),
    (81, 5, '5. 손질된 오징어 속 안에 준비한 속을 채워 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_5.png'),
    (81, 6, '6. 오징어순대에 레몬즙과 정종, 생강즙을 섞어 바른 뒤 찜통에 김이 오르면 오징어순대를 넣어 약 10분 정도 쪄서 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_6.png'),
    (82, 1, '1. 모시조개는 물에 소금을 넣고, 담그어 해감을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_1.png'),
    (82, 2, '2. 연어는 사각지게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_2.png'),
    (82, 3, '3. 당근, 양파, 감자는 사각크기로 썰어 놓고, 청피망은 채를 썰어 당근과 비슷한 길이로 잘라둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_3.png'),
    (82, 4, '4. 냄비에 버터와 밀가루를 넣고 볶다가 채소를 넣어 같이 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_4.png'),
    (82, 5, '5. 볶아지는 냄비에 모시조개와 육수를 넣고 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_5.png'),
    (82, 6, '6. 거품을 걷어내고, 월계수 잎과 통후추, 통마늘을 넣고 끓이다가 연어와 생크림을 넣어 한 소큼 끓으면 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_6.png'),
    (83, 1, '1. 시금치와 브로콜리는 끓는 물에 소금을 넣고 살짝 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_1.png'),
    (83, 2, '2. 데친 시금치, 브로콜리와 바질을 믹서에 갈아 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_2.png'),
    (83, 3, '3. 새우는 껍질을 벗겨 손질한다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_3.png'),
    (83, 4, '4. 끓는 물에 스파게티를 10분 정도 삶아 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_4.png'),
    (83, 5, '5. 삶아진 스파게티는 팬에 올리브오일을 넣고 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_5.png'),
    (83, 6, '6. 갈아 놓은 시금치소스에 생크림을 넣고 한소끔 끓이면서 볶은 파스타를 넣은 뒤 새우를 올린다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_6.png'),
    (84, 1, '1. 광어는 비늘을 제거하고 포를 뜬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_1.png'),
    (84, 2, '2. 손질된 광어에 레몬과 청주를 뿌려 숙성 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_2.png'),
    (84, 3, '3. 저염간장에 레몬즙, 와사비, 발사믹소스를 넣고 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_3.png'),
    (84, 4, '4. 당근, 애호박은 쿠퍼로 동글동글하게 깎는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_4.png'),
    (84, 5, '5. 당근, 애호박, 콜리플라워는 김이 오른 찜통에 5분 정도 쪄 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_5.png'),
    (84, 6, '6. 가지와 파프리카는 팬에 굽고, 숙성시킨 광어도 팬에 구워 접시에 담고 쪄 낸 야채를 담은 뒤 소스를 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_6.png'),
    (85, 1, '1. 연근은 껍질을 벗긴다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_1.png'),
    (85, 2, '2. 껍질벗긴 연근은 얇게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_2.png'),
    (85, 3, '3. 물 200g에 식초 10g을 섞는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_3.png'),
    (85, 4, '4. 식촛물에 썬 연근을 담근다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_4.png'),
    (85, 5, '5. 연근을 체에 건져 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_5.png'),
    (85, 6, '6. 연근을 바삭하게 튀긴 뒤 천일염을 살짝 뿌리고, 계피가루를 묻힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_6.png'),
    (86, 1, '1. 쌀은 깨끗이 씻어 30분 정도 불려 밥을 짓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_1.png'),
    (86, 2, '2. 달걀을 풀어 소금을 넣고 지단을 만들어 채썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_2.png'),
    (86, 3, '3. 도라지, 애호박, 양파, 홍고추는 채를 썰어 소금에 살짝 절여 볶고, 청포묵은 채를 썰어 참기름에 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_3.png'),
    (86, 4, '4. 돼지 안심은 소금, 후춧가루로 양념 한 뒤 팬에 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_4.png'),
    (86, 5, '5. 찬물에 식초, 설탕, 소금을 넣고 채 썬 오이를 넣어 냉국을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_5.png'),
    (86, 6, '6. 고추장에 고춧가루를 섞어 쌈밥장을 만들고, 라이스페이퍼를 물에 담궈 건져 밥과 준비한 야채, 달걀지단, 쌈무, 쌈밥장을 올려 돌돌 말고 데친 미나리로 묶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_6.png'),
    (87, 1, '1. 닭은 살을 발라 소금, 후춧가루로 밑간을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_1.png'),
    (87, 2, '2. 오곡을 불려 밥을 짓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_2.png'),
    (87, 3, '3. 오이, 아스파라거스, 당근, 양파는 길이대로 채썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_3.png'),
    (87, 4, '4. 닭살에 지어 놓은 오곡밥을 넣는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_4.png'),
    (87, 5, '5. 오곡밥을 넣은 닭살에 카레가루를 골고루 묻힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_5.png'),
    (87, 6, '6. 팬에 기름을 두르고 5번을 올려 은근히 익혀 접시에 담고, 썰어 놓은 야채를 닭살위에 담은 뒤 디종머스터드를 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_6.png'),
    (88, 1, '1. 광어는 비늘을 벗기고 포를 떠 소금, 후춧가루를 뿌린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_1.png'),
    (88, 2, '2. 콜리플라워는 팬에 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_2.png'),
    (88, 3, '3. 감자와 양파를 썰어 콜리플라워와 함께 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_3.png'),
    (88, 4, '4. ?번에 우유를 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_4.png'),
    (88, 5, '5. ?를 믹서로 곱게 간 뒤, 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_5.png'),
    (88, 6, '6. 가지, 당근. 단호박은 둥근 모양으로 만들어 팬에 시금치와 함께 익히고, 광어도 팬에 구워 접시에 담아 컬리플라워 소스를 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_6.png'),
    (89, 1, '1. 다시마와 건새우를 넣고 은근히 끓여 차갑게 식힌 뒤 육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_1.png'),
    (89, 2, '2. 차갑게 식힌 육수는 체에 걸려내고 오미자를 넣고 소금으로 간을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_2.png'),
    (89, 3, '3. 콜라비, 피망, 파프리카는 납작하게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_3.png'),
    (89, 4, '4. 육수는 체에 거른다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_4.png'),
    (89, 5, '5. 오미자를 넣고 소금으로 간을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_5.png'),
    (89, 6, '6. 통에 썰어놓은 야채와 무화과를 담고 ?를 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_6.png'),
    (90, 1, '1. 닭가슴살은 포를 떠 소금과 후춧가루로 밑간을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_1.png'),
    (90, 2, '2. 새우는 껍질을 벗겨 손질한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_2.png'),
    (90, 3, '3. 베이컨은 끓는 물에 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_3.png'),
    (90, 4, '4. 마늘과 바질은 다진 뒤 버터에 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_4.png'),
    (90, 5, '5. 닭가슴살에 베이컨과 다진 버터를 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_5.png'),
    (90, 6, '6. 냄비에 마늘과 바질을 섞은 버터, 치즈를 넣고 끓이다가 ?를 놓고 졸인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_6.png'),
    (91, 1, '1. 표고버섯은 따뜻한 물에 불린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_1.png'),
    (91, 2, '2. 감자는 강판에 간다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_2.png'),
    (91, 3, '3. 고수는 잘게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_3.png'),
    (91, 4, '4. 당근은 곱게 다지고, 양파와 홍고추도 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_4.png'),
    (91, 5, '5. 감자에 다진 당근과 양파, 홍고추를 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_5.png'),
    (91, 6, '6. 불린 표고버섯은 기둥을 자르고 바닥에 녹말가루를 묻혀 ?번을 넣고 김이 오른 찜통에 약 10분 정도 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_6.png'),
    (92, 1, '1. 새우는 껍질을 벗겨 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_1.png'),
    (92, 2, '2. 오징어는 소금으로 껍질을 벗겨 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_2.png'),
    (92, 3, '3. 애호박, 깻잎, 대추는 곱게 채썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_3.png'),
    (92, 4, '4. 채썬 애호박과 깻잎은 소금에 살짝 절인 뒤 물기를 짠다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_4.png'),
    (92, 5, '5. 밀가루에 찹쌀가루, 애호박, 깻잎, 대추채와 다진 새우, 오징어를 넣고 팬에 기름을 둘러 전병을 만든 뒤 돌돌 말아 썰어 접시에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_5.png'),
    (92, 6, '6. 저염간장과 식초, 설탕을 넣고 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_6.png'),
    (93, 1, '1. 닭다리는 기름기를 제거하고 냄비에 물 3컵 반을 부어 통마늘과 삶아 육수를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-1.jpg'),
    (93, 2, '2. 삶은 마늘을 체에 걸러 으깬 뒤 다시 육수에 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-2.jpg'),
    (93, 3, '3. 닭 육수 2컵 반에 찬밥을 넣고 밥알이 무르도록 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-3.jpg'),
    (93, 4, '4. 삶은 닭다리 살을 발라 잘게 찢어 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-4.jpg'),
    (93, 5, '5. 감자, 양파, 당근, 부추는 잘게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-5.jpg'),
    (93, 6, '6. 4에 5를 넣고 끓인 뒤 소금, 후춧가루로 간 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-6.jpg'),
    (94, 1, '1. 물 1컵 반에 국멸치와 건다시마를 넣어 멸치다시마국물을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-1.jpg'),
    (94, 2, '2. 국물을 우린 다시마는 채썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-2.jpg'),
    (94, 3, '3. 느타리버섯은 끓는 물에 데친 후 찢는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-3.jpg'),
    (94, 4, '4. 감자와 두부는 3~4cm 가량으로 채를 썰고 홍고추와 대파는 어슷썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-4.jpg'),
    (94, 5, '5. 멸치다시마국물 1컵에 감자를 넣고 끓인 뒤 느타리버섯을 넣어 한소끔 끓이고 두부, 파, 다진 마늘, 홍고추를 넣고 소금으로 간 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-5.jpg'),
    (94, 6, '6. 채 썬 다시마를 고명으로 얹는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-6.jpg'),
    (95, 1, '1. 양념장 재료를 모두 섞어 냉장고에서 하루 숙성시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-1.jpg'),
    (95, 2, '2. 민어는 비늘을 긁고 지느러미, 내장을 제거하여 흐르는 물에 씻어 5cm 길이로 토막 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-2.jpg'),
    (95, 3, '3. 소고기는 채 썰어 다진 마늘, 후추로 밑간한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-3.jpg'),
    (95, 4, '4. 무는 나박 썰기, 애호박은 반달 썰기, 애느타리는 한입 크기로 찢고 고추와 대파는 어슷 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-4.jpg'),
    (95, 5, '5. 냄비에 참기름을 두르고 소고기를 볶다가 물 1컵 반과 무를 넣고 양념장을 풀어 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-5.jpg'),
    (95, 6, '6. 무가 반 정도 익으면 민어와 채소를 넣고 한소끔 끓인 뒤 어슷 썬 대파와 생강즙을 넣어 살짝 끓이고 소금 간하고 쑥갓을 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-6.jpg'),
    (96, 1, '1. 냄비에 육수 재료와 물(400g)을 넣고 푹 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_1.png'),
    (96, 2, '2. 양송이버섯을 잘게 다진 뒤 버터에 살짝 볶아 건져 한김 식힌 뒤 닭다리살과 닭가슴살, 아몬드를 다져 볶은 양송이버섯과 섞어 동그랗게 빚어 밀가루옷을 입힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_2.png'),
    (96, 3, '3. 식용유(15g)를 두른 팬에서 겉을 노릇하게 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_3.png'),
    (96, 4, '4. 육수(150g), 우유, 들깻가루를 끓여 소스를 만든 뒤 아몬드 치킨볼과 브로콜리를 넣고 졸인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_4.png'),
    (96, 5, '5. 마늘은 편 썰어 낮은 온도의 식용유에서 바삭하게 튀긴다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_5.png'),
    (96, 6, '6. 접시에 아몬드치킨볼을 담고 튀긴 마늘을 얹어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_6.png'),
    (97, 1, '1. 현미와 쌀을 씻어 30분간 불린 뒤 물기를 빼고, 다시마를 우려낸 물(불린 쌀 동량)로 밥을 지어 식힌 후 새우가루를 넣어 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_1.png'),
    (97, 2, '2. 양배추는 15~20분 정도 쪄서 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_2.png'),
    (97, 3, '3. 얇게 저민 쇠고기를 팬에 올려 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_3.png'),
    (97, 4, '4. 달래는 0.5㎝ 정도로 송송 썰어 간장, 참기름에 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_4.png'),
    (97, 5, '5. 김발에 양배추, 고기, 밥 순서대로 올려 돌돌 말아 양배추롤을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_5.png'),
    (97, 6, '6. 롤을 한입 크기로 썰어 담고 달래 무침을 얹은 뒤 송송 썬 붉은 고추를 올려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_6.png'),
    (98, 1, '1. 피망, 파프리카는 5㎝ 길이로 채 썰고, 시금치는 갈아둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_1.png'),
    (98, 2, '2. 베이컨 위에 동태포를 올리고 그 위에 피망과 파프리카를 올려 돌돌 만다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_2.png'),
    (98, 3, '3. 저염버터에 생크림을 넣어서 농도를 맞춘 후 갈아놓은 시금치를 넣어 시금치소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_3.png'),
    (98, 4, '4. 달군 팬에 흰살생선베이컨말이를 올려 완전히 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_4.png'),
    (98, 5, '5. 접시에 담아 시금치소스를 곁들인 뒤 새싹채소로 장식하여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_5.png'),
    (99, 1, '1. 콩고기를 15분~20분 정도 물에 불려 물기를 완전히 제거한 뒤 저염간장양념으로 버무려 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_1.png'),
    (99, 2, '2. 아보카도, 바나나, 가지, 연근은 깍둑 썰고, 방울토마토는 반을 가른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_2.png'),
    (99, 3, '3. 팬에 식용유(5g)를 둘러가며 방울토마토, 가지, 연근을 각각 볶아 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_3.png'),
    (99, 4, '4. 만두피를 작은 체에 넣고 바구니 모양으로 만들어 튀긴다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_4.png'),
    (99, 5, '5. 콩고기와 아보카도, 바나나, 방울토마토, 가지, 연근을 튀긴 만두피에 담은 뒤 허브오일드레싱을 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_5.png'),
    (100, 1, '1. 수박껍질은 흰 부분만 사용해 작게 깍둑 썰고, 토마토는 껍질을 벗겨 속씨를 제거해 작게 깍둑썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_1.png'),
    (100, 2, '2. 닭가슴살은 수비드해 익힌 뒤 한입 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_2.png'),
    (100, 3, '3. 감자, 가지, 양파, 피망, 아스파라거스는 깍둑 썬 뒤 닭가슴살과 함께 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_3.png'),
    (100, 4, '4. 팬에 복분자를 넣고 살짝 졸인 뒤 레몬즙을 섞어 복분자소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_4.png'),
    (100, 5, '5. 접시에 퀴노아를 두른 후 닭가슴살과 감자, 가지, 양파, 피망, 아스파라거스를 올리면서 중간 중간에 토마토와 수박, 리코타치즈를 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_5.png'),
    (100, 6, '6. 맨 위에 퀴노아를 살짝 올려주고 양상추, 플레인 요구르트, 어린잎채소를 얹어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_6.png'),
    (101, 1, '1. 바나나를 반으로 잘라 반은 편 썰고 반은 으깨고, 아보카도는 껍질과 씨를 제거 한 후 ⅓은 편 썰고 ⅔는 으깬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_1.png'),
    (101, 2, '2. 토마토는 끓는 물에 데쳐 껍질을 제거하고 얇게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_2.png'),
    (101, 3, '3. 베이컨 2장은 채 썰고, 1장은 구운 뒤 비슷한 크기로 채 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_3.png'),
    (101, 4, '4. 베이컨을 구운 기름에 편 썬 바나나, 아보카도, 토마토를 넣고 바질가루를 뿌려 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_4.png'),
    (101, 5, '5. 팬에 채 썬 베이컨과 다진 마늘, 다진 양파를 볶은 후 휘핑크림, 저염치즈를 넣고 볶다가 으깬 아보카도, 바나나, 우유를 넣어 농도를 맞추고 소금으로 간해 바나나크림을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_5.png'),
    (101, 6, '6. 접시에 구운 바나나, 아보카도, 토마토를 깔고 삶은 스파게티면을 담은 후 바나나크림을 뿌리고 베이컨을 올려 장식해 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_6.png'),
    (102, 1, '1. 신선초, 참나물, 취나물, 적겨자는 먹기 좋은 크기로 뜯고, 깻잎은 한입 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_1.png'),
    (102, 2, '2. 양파(8g)는 채 썰고, 파스타소스에 들어갈 양파(15g)는 굵게 다지고, 애호박도 양파와 비슷한 크기로 굵게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_2.png'),
    (102, 3, '3. 오이는 돌려 깎은 뒤 채 썰고, 고추도 얇게 채 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_3.png'),
    (102, 4, '4. 끓는 물에 쌀파스타면을 넣고 5분간 삶아 익힌 뒤 찬물에 헹궈 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_4.png'),
    (102, 5, '5. 팬에 물(60g)을 붓고 물이 끓으면 된장, 청국장, 다진 양파, 애호박, 으깬 두부, 다진 마늘을 넣고 1분 30초 정도 졸여 파스타소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_5.png'),
    (102, 6, '6. 쌀파스타면 위에 소스를 얹은 뒤 채소를 올리고 들기름을 뿌려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_6.png'),
    (103, 1, '1. 감자는 한입 크기로 썰고, 양파와 당근, 대파는 길게 채 썰고, 청양고추는 다지고, 팽이버섯은 깨끗이 씻어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_1.png'),
    (103, 2, '2. 달걀은 노른자와 흰자를 분리해 지단을 부쳐 채 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_2.png'),
    (103, 3, '3. 당면은 찬물에 2시간 정도 불린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_3.png'),
    (103, 4, '4. 물(300g)에 멸치를 넣어 육수를 만든 후, 황태포를 넣어 다시 한 번 진하게 육수를 우려낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_4.png'),
    (103, 5, '5. 육수에 감자, 당근, 양파, 당면을 순서대로 넣고 끓인 후 불을 끄고 팽이버섯을 얹어 잔열로 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_5.png'),
    (103, 6, '6. 다진 마늘, 청양고추, 저염간장, 소금, 후춧가루를 넣어 간을 한 뒤 지단, 쑥갓, 대파를 얹어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_6.png'),
    (104, 1, '1. 마늘 5g은 다지고, 나머지 마늘(15g)은 편 썰어 노릇하게 튀긴다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_1.png'),
    (104, 2, '2. 깻잎은 채 썰고, 쪽파는 송송 썰고, 파프리카는 주사위 모양으로 썰고, 양파와 대파는 채 썰고, 방울토마토는 반을 갈라 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_2.png'),
    (104, 3, '3. 닭가슴살은 어슷하게 납작 썰어 이태리시즈닝에 재운 뒤 팬에 구워 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_3.png'),
    (104, 4, '4. 다진 마늘, 양파, 대파를 팬에 넣고 향을 내다 파프리카와 방울토마토, 양념을 넣고 볶은 뒤 한 김 식혀 믹서에 곱게 갈아 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_4.png'),
    (104, 5, '5. 팬에 소스와 구운 닭가슴살을 넣어 볶다가 삶은 라면을 넣고 재빠르게 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_5.png'),
    (104, 6, '6. 튀긴 마늘과 깻잎, 쪽파를 올려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_6.png'),
    (105, 1, '1. 양파와 청경채, 파프라카는 채 썰고, 쪽파는 송송 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_1.png'),
    (105, 2, '2. 라면은 삶아서 체에 밭쳐 물기를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_2.png'),
    (105, 3, '3. 팬에 식용유(5g)를 두르고 다진 마늘을 볶아 향을 낸 뒤 돼지고기를 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_3.png'),
    (105, 4, '4. 채 썬 양파와 청경채, 파프리카를 함께 넣고 볶다가 고추기름, 설탕, 두반장, 라면스프로 간을 해 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_4.png'),
    (105, 5, '5. 면을 함께 볶은 후 송송 썬 쪽파를 뿌려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_5.png'),
    (106, 1, '1. 새우, 조개, 홍합은 손질한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_1.png'),
    (106, 2, '2. 버섯은 먹기 좋은 크기로 썰고, 양파는 채 썰고, 토마토는 씨를 제거해 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_2.png'),
    (106, 3, '3. 라면은 끓는 물에 데친 뒤 체에 밭쳐 물기를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_3.png'),
    (106, 4, '4. 냄비에 식용유(10g)를 두른 뒤 새우와 조개, 홍합을 넣어 살짝 볶아 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_4.png'),
    (106, 5, '5. 같은 냄비에 토마토, 우유, 생크림을 넣은 뒤 월계수잎과 정향을 넣고 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_5.png'),
    (106, 6, '6. 끓으면 데친 라면, 새송이버섯, 홍합, 새우, 조개, 양파를 넣고 끓여 다 익으면 파르메산 치즈가루를 뿌린 후 그 위에 파슬리가루를 뿌려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_6.png'),
    (107, 1, '1. 라면을 살짝 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_1.png'),
    (107, 2, '2. 배와 파인애플을 한입 크기로 썰고, 파프리카는 깍둑 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_2.png'),
    (107, 3, '3. 팬에 쇠고기 양념장과 파인애플을 넣어 끓이다가 채 썬 쇠고기를 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_3.png'),
    (107, 4, '4. 레드와인을 넣고 알코올을 날린 뒤 매실청을 넣고 조린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_4.png'),
    (107, 5, '5. 라면과 파프리카, 배를 섞은 후 라이스페이퍼에 올려 만다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_5.png'),
    (107, 6, '6. 달걀물을 묻힌 후 빵가루를 입혀 튀긴 뒤 그릇에 어린잎채소와 함께 담아 조린 쇠고기를 곁들여 마무리 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_6.png'),
    (108, 1, '1. 돼지고기를 차가운 물로 헹구고 냄비에 물(300g), 대파, 생강과 같이 넣고 끓여 돼지고기가 익으면 면포에 거른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_1.png'),
    (108, 2, '2. 냄비에 물(300g)을 넣어 끓으면 가다랑어포를 넣고 1분간 끓이고 불을 꺼 가다랑어포가 가라앉으면 면포에 거르고, 다시 물(300g)을 넣어 면포에 걸러낸 가다랑어포를 넣어 약한 불로 10분간 끓인 뒤 가다랑어포가 가라앉으면 면포에 거른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_2.png'),
    (108, 3, '3. 돼지고기 육수와 가다랑어포 육수를 섞은 뒤 200g을 따로 덜어 조림양념을 넣고 끓이다가 돼지고기를 넣고 조린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_3.png'),
    (108, 4, '4. 라면은 삶아 찬물에 헹궈 건지고, 쪽파는 송송 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_4.png'),
    (108, 5, '5. 육수를 뜨겁게 끓인 뒤 그릇에 라면사리와 함께 담고, 돼지고기조림, 쪽파를 올리고 팬에 남은 조림 소스를 끼얹어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_5.png'),
    (109, 1, '1. 끓는 물(600g)에 라면을 넣어 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00310_1.png'),
    (109, 2, '2. 면은 찬물에 헹궈 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00310_2.png'),
    (109, 3, '3. 양상추, 케일, 파프리카는 얇게 채 썰고, 비타민은 먹기 좋은 크기로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00310_3.png'),
    (109, 4, '4. 유자드레싱을 고루 섞은 뒤 라면, 채소를 버무려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00310_4.png'),
    (110, 1, '1. 주꾸미를 몸과 다리를 분리 후 잘 씻어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_01.png'),
    (110, 2, '2. 주꾸미 다리, 미나리, 양파, 당근을 다져준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_02.png'),
    (110, 3, '3. 다진 채소를 계란, 다진마늘, 생강청에 양념 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_03.png'),
    (110, 4, '4. 양념한 3을 주꾸미 머리 속에 넣어주고 이쑤시개로 꽂아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_04.png'),
    (110, 5, '5. 찜기에 찐다. 찔 때 레몬즙을 뿌리면 더욱 탱탱하다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_05.png'),
    (110, 6, '6. 소스를 만든 뒤 주꾸미를 함께 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_06.png'),
    (111, 1, '1. 무는 모양 틀로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_01.png'),
    (111, 2, '2. 무와 연근을 손질해 놓은 후 소금에 살짝 절여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_02.png'),
    (111, 3, '3. 사과와 배는 먹기 좋게 잘라 레몬즙에 뿌려 갈변을 막아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_03.png'),
    (111, 4, '4. 물에 청양고추를 넣어 끓여서 식힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_04.png'),
    (111, 5, '5. 식힌 물에 국물 재료를 넣어 국물을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_05.png'),
    (111, 6, '6. 만들어진 국물에 절여진 무, 연근, 사과, 배를 넣어주어 완성한다. 24시간 이후부터 시원하게 먹는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_06.png'),
    (112, 1, '1. 다진마늘을 버터에 충분히 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_01.png'),
    (112, 2, '2. 화이트와인을 뿌려주고 소스재료로 소스를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_02.png'),
    (112, 3, '3. 찜기에 살짝 져 준다. 이때 80% 정도까지만 익혀준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_03.png'),
    (112, 4, '4. 쪄준 조기는 와인과 후추로 밑간을 해준 후 전분을 발라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_04.png'),
    (112, 5, '5. 전분를 바른 조기에 허브크러스트를 두툼하게 올려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_05.png'),
    (112, 6, '6. 팬에 기름을 두르고 조기를 한쪽부터 구운 뒤, 뒤집어서 다시 구워주고 소스를 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_06.png'),
    (113, 1, '1. 무, 청고추, 미나리, 대파는 먹기 좋게 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_01.png'),
    (113, 2, '2. 돼지머리는 냉수에 데친다. 냉수일 때부터 돼지머리를 넣어야 물이 끓어오르면 불순물이 제거된다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_02.png'),
    (113, 3, '3. 일본 된장을 풀어 체에 걸러준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_03.png'),
    (113, 4, '4. 돼지머리를 살짝 졸여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_04.png'),
    (113, 5, '5. 냄비에 국물을 끓여주다가 채소를 넣어 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_05.png'),
    (113, 6, '6. 국물에 우동면을 데쳐낸 뒤 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_06.png'),
    (114, 1, '1. 양파, 2가지색 파프리카, 청고추는 작은 깍두기 모양으로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_01.png'),
    (114, 2, '2. 파인애플도 먹기 좋게 썰어준 뒤 레몬소금을 살짝 뿌려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_02.png'),
    (114, 3, '3. 계란은 풀어서 팬에 스크램블을 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_03.png'),
    (114, 4, '4. 마늘을 다진 후 마늘기름으로 칵테일 새우를 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_04.png'),
    (114, 5, '5. 볶아진 칵테일새우에 잘라준 채소를 넣어 다시 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_05.png'),
    (114, 6, '6. 여기에 밥을 넣어서 볶아주고 파인애플과 맛간장, 통깨를 넣어서 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_06.png'),
    (115, 1, '1. 양파는 채 썰어 물에 담가주고, 오이는 돌려 깎기 해서 물에 담가준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_01.png'),
    (115, 2, '2. 연근은 껍질을 까고 얇게 썰어서 식초 2방울을 넣은 물에 데쳐준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_02.png'),
    (115, 3, '3. 밤도 아주 얇게 썰어서 물에 담가준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_03.png'),
    (115, 4, '4. 소스 재료를 모두 넣고 믹서에 간다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_04.png'),
    (115, 5, '5. 물에 데쳐낸 연근은 키친타월에 올려서 물기를 완전히 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_05.png'),
    (115, 6, '6. 모든 재료를 넣고 소스에 버무려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_06.png'),
    (116, 1, '1. 코다리는 어간장, 청주, 흰후추, 생강청에 살짝 재워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_01.png'),
    (116, 2, '2. 함초는 손질해 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_02.png'),
    (116, 3, '3. 무는 소금에 살짝 절여서 수분과 염분을 제거해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_03.png'),
    (116, 4, '4. 무와 함초를 고루 섞어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_04.png'),
    (116, 5, '5. 미나리와 콩나물은 손질해준 후 찜기에 깔고 코다리를 쪄준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_05.png'),
    (116, 6, '6. 소스를 만든 뒤 채소를 깔고 코다리를 올리고 미나리, 콩나물을 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_06.png'),
    (117, 1, '1. 두부는 물기를 꼭 짠 뒤 으깨어 전분, 계란과 섞어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_01.png'),
    (117, 2, '2. 우엉, 인삼, 감자는 아주 곱게 채 썰어서 냉수에 담가준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_02.png'),
    (117, 3, '3. 볶아진 모든 채소를 각각 볶아서 한데 섞어 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_03.png'),
    (117, 4, '4. 땅콩잼으로 소스를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_04.png'),
    (117, 5, '5. 김발을 깔고 비닐을 놓아 준 후 두부를 펴준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_05.png'),
    (117, 6, '6. 볶아진 채소를 두부에 넣어주고 찜기에 쪄서 식힌 뒤 한입 크기로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_06.png'),
    (118, 1, '1. 사과는 8조각으로 잘라 씨를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_01.png'),
    (118, 2, '2. 청고추, 홍고추는 어슷하게 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_02.png'),
    (118, 3, '3. 양파도 먹기 좋은 크기로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_03.png'),
    (118, 4, '4. 사과는 식초 물에 살짝 절여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_04.png'),
    (118, 5, '5. 양념장을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_05.png'),
    (118, 6, '6. 양념장에 준비된 재료를 모두 섞어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_06.png'),
    (119, 1, '1. 주꾸미, 전복, 칵테일새우는 각각 물에 데쳐서 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_01.png'),
    (119, 2, '2. 이때 데치고 남은 물을 국물에 이용해주고 여기에 청주를 넣어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_02.png'),
    (119, 3, '3. 양파, 청고추, 홍고추는 먹기좋게 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_03.png'),
    (119, 4, '4. 계란을 황백으로 부쳐서 먹기좋게 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_04.png'),
    (119, 5, '5. 만들어진 국물에 거피한 들깨가루와 국물재료를 넣어 끓여서 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_05.png'),
    (119, 6, '6. 준비된 재료를 냄비에 넣고 국물을 부어 끓여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_06.png'),
    (120, 1, '1. 참외는 껍질을 벗기고 반으로 잘라 속을 파 준 후 먹기 좋게 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_01.png'),
    (120, 2, '2. 청고추, 홍고추는 송송 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_02.png'),
    (120, 3, '3. 양파와 미나리는 참외크기로 자르고, 대파는 송송 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_03.png'),
    (120, 4, '4. 참외, 양파를 저염 간장에 살짝 절여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_04.png'),
    (120, 5, '5. 절여진 채소에 고춧가루로 먼저 색을 입힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_05.png'),
    (120, 6, '6. 나머지 재료를 넣고 김치를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_06.png'),
    (121, 1, '1. 연어는 얇게 모양을 살려 슬라이스 한 후 정종에 소금을 섞어 분무기에 넣어 냉장고에 넣어둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_01.png'),
    (121, 2, '2. 건조된 방울토마토는 올리브오일과 후추로 양념하여 사용한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_02.png'),
    (121, 3, '3. 양상추는 먹기 좋은 크기로 뜯어 어린잎과 함께 찬물에 담가 물기를 제거한 후 사용한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_03.png'),
    (121, 4, '4. 유자청과 감식초는 함께 섞어 믹서에 갈아 드레싱소스를 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_04.png'),
    (121, 5, '5. 양파는 모양을 살려 얇게 슬라이스 하여 찬물에 담가 물기를 제거한 뒤 사용한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_05.png'),
    (121, 6, '6. 샐러드 접시에 준비된 모든 채소를 담고, 연어는 장미 모양으로 접어서 보기좋게 담아 드레싱 소스를 곁들여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_06.png'),
    (122, 1, '1. 콜라비는 껍질을 벗긴 후 저염간장에 30분 정도 절여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_01.png'),
    (122, 2, '2. 우렁은 뜨거운 물에 데친 후 행군 뒤 물기를 빼준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_02.png'),
    (122, 3, '3. 미나리는 먹기 좋은 크기로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_03.png'),
    (122, 4, '4. 다진대파, 청홍고추, 양파는 송송 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_04.png'),
    (122, 5, '5. 양념장에 콜라비부터 버무린 뒤 모든 재료를 섞어서 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_05.png'),
    (123, 1, '1. 코다리는 먹기 좋게 잘라서 물에 헹궈 물기를 빼준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_01.png'),
    (123, 2, '2. 감자와 무, 백일송이버섯, 미나리는 먹기 좋게 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_02.png'),
    (123, 3, '3. 양파, 청양고추도 먹기 좋게 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_03.png'),
    (123, 4, '4. 냄비에 참기름을 넣어 코다리를 살짝 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_04.png'),
    (123, 5, '5. 코다리를 볶은 냄비에 무, 감자, 국물 재료를 넣고 충분히 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_05.png'),
    (123, 6, '6. 끓고 있는 국에 양파, 청양고추, 백일송이 버섯을 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_06.png'),
    (124, 1, '1. 연두부는 동글게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_1.png'),
    (124, 2, '2. 호박은 갈라 씨를 제거 하고 껍질을 벗겨 쪄서 작은 깍둑썰기로 썰어 준비 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_2.png'),
    (124, 3, '3. 바나나와 파프리카 토마토는 호박과 같은 크기의 작은 깍두기로 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_3.png'),
    (124, 4, '4. 닭고기살은 통후추를 넣고 약 20분 정도 삶아 건져 작은 깍두기로 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_4.png'),
    (124, 5, '5. 마와 잣, 흑임자를 갈아 놓은 뒤 생크림, 요거트, 식초, 올리브오일, 설탕을 섞어 샐러드 소스를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_5.png'),
    (124, 6, '6. 썰어 놓은 연두부 위에 호박 , 닭고기, 토마토, 파프리카를 각각 올린 뒤 어린 잎을 올리고 소스를 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_6.png'),
    (125, 1, '1. 애호박은 반달 모양으로 썰어 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_1.png'),
    (125, 2, '2. 썰어 놓은 호박에 소금을 넣고 살짝 절여 물기를 제거하고 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_2.png'),
    (125, 3, '3. 양파는 채를 썰고, 대파는 1/4로 자르고, 홍고추는 어슷썰어 자연스럽게 씨를 제거해 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_3.png'),
    (125, 4, '4. 팬에 들기름을 넣고, 마늘을 먼저 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_4.png'),
    (125, 5, '5. 볶아진 팬에 건새우를 넣어 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_5.png'),
    (125, 6, '6. 건새우가 볶아지면, 썰어 놓은 호박을 넣어 볶다가 양파와 대파를 넣어 볶으면서 들깨 가루와 넣어 살짝 볶으면서 마지막에 썰어 놓은 홍고추를 넣어 한 번만 더 볶아 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_6.png'),
    (126, 1, '1. 고구마는 찜통에 약 20분 정도 쪄서 껍질을 벗겨 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_1.png'),
    (126, 2, '2. 뜨거운 고구마가 한김 빠지면, 곱게 으깨 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_2.png'),
    (126, 3, '3. 으깨진 고구마에 바나나를 썰어 요거트와 함께 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_3.png'),
    (126, 4, '4. 3번에 다시 연유를 넣어 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_4.png'),
    (126, 5, '5. 잣은 종이를 깔고 곱게 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_5.png'),
    (126, 6, '6. 어린잎은 물에 담그어, 건져 물기를 제거해 놓고, 으깬 고구마에 다진 잣을 넣어 샐러드를 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_6.png'),
    (127, 1, '1. 새우는 껍질을 제거하고 소금과 후춧가루를 넣어 밑간을 해놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_1.png'),
    (127, 2, '2. 어린잎은 찬물에 담그어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_2.png'),
    (127, 3, '3. 튀김가루에 달걀을 넣어 잘 섞어 튀김반죽을 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_3.png'),
    (127, 4, '4. 밑간을 해 놓은 새우를 튀김반죽에 넣어 옷을 입힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_4.png'),
    (127, 5, '5. 누룽지는 170℃의 온도 튀김 기름에 튀겨내고, 튀김옷 입힌 새우도 튀겨낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_5.png'),
    (127, 6, '6. 냄비에 오렌지주스와 설탕, 식초를 넣어 은근히 졸여 소스를 만들고, 어린잎은 찬물에 건져 물기를 제거하고 접시에 깔고 튀긴 누룽지와 튀긴 새우를 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_6.png'),
    (128, 1, '1. 백김치는 살짝 씻어 물기를 제거하고 잘게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_1.png'),
    (128, 2, '2. 양파와 마늘은 다지고 파는 채를 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_2.png'),
    (128, 3, '3. 어린잎은 찬 물에 담그어 놓았다가, 다시 소창에 건져 물기를 빼 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_3.png'),
    (128, 4, '4. 냄비에 물을 넣고, 양파와 파를 넣어 끓이다가 준비한 쭈구미를 넣고 부드럽게 데쳐 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_4.png'),
    (128, 5, '5. 식초와 간장, 설탕, 꿀, 레몬, 다진양파, 다진마늘을 넣고 잘 섞어서 샐러드 드레싱을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_5.png'),
    (128, 6, '6. 준비한 백김치(①)와 주꾸미(②)를 섞은 후 양상추를 접시에 깔고, 그 위에 섞어놓은 백김치와 주꾸미를 올리고, 홍고추와 어린잎을 위에 장식으로 올린 후 샐러드 소스를 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_6.png'),
    (129, 1, '1. 닭고기살에 로즈마리를 뿌려 약 3시간 이상 숙성을 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_1.png'),
    (129, 2, '2. 냄비에 물을 1/3쯤 넣고, 양파채를 썰어 넣어 끓으면, 숙성된 닭고기살을 넣고 약 20분 정도 삶아 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_2.png'),
    (129, 3, '3. 삶아 건진 닭고기살을 한 김 빼고, 길게 손으로 찢어 소금과 후춧가루에 살짝 무치고, 오이와 파프리카는 씨를 제거 하고 채를 썰고, 당근도 오이채의 길이에 맞게 채를 썰어 준비해 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_3.png'),
    (129, 4, '4. 마늘을 다지고, 고춧가루와 설탕, 식초, 레몬즙을 넣어 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_4.png'),
    (129, 5, '5. 뜨거운 물에 라이스페이퍼를 살짝 넣어 바로 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_5.png'),
    (129, 6, '6. 건져 놓은 라이스페이퍼에 준비한 닭고기살과 파프리카, 오이, 당근 넣고 돌돌 말아, 녹말가루를 살짝 입혀, 팬에 굴리듯 익혀 완성하고, 준비해 놓은 소스와 함께 제공한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_6.png'),
    (130, 1, '1. 닭고기살은 천일염과 후춧가루를 뿌려 마리네이드를 해놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00536_1.png'),
    (130, 2, '2. 양배추와 부추는 김이 오른 찜통에 쪄서 식혀 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00536_2.png'),
    (130, 3, '3. 깻잎은 채를 썰고, 파프리카는 속을 제거 하고 5cm 길이로 채를 썰고, 당근과 양파도 같은 길이로 채를 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00536_3.png'),
    (130, 4, '4. 마리네이드를 해 놓은 닭고기살은 팬에 기름을 두르고, 팬이 따끈해지면 앞뒤를 돌려가며 정종을 살살 뿌려 닭고기살을 굽고, 닭고기살이 익으면 살을 찢어 준비 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00536_4.png'),
    (130, 5, '5. 요거트와 머스터드, 꿀 ,레몬즙, 설탕을 넣고 고루 섞어 소스를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00536_5.png'),
    (130, 6, '6. 쪄 놓은 양배추에 준비한 채소(③)와 닭고기살(④)을 넣고, 돌돌 말아 쪄 놓은 부추로 양배추를 묶어 완성하고, 만들어 놓은 요거트 소스를 함께 제공 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00536_6.png'),
    (131, 1, '1. 단호박은 1/8 등분으로 갈라 속을 파내고 찜통에서 약 15~20분 정도 충분히 쪄낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_1.png'),
    (131, 2, '2. 소고기는 채를 썰어 마늘, 간장, 설탕에 양념을 하고 팬에 볶아서 식힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_2.png'),
    (131, 3, '3. 파프리카와 오이, 적양배추는 채를 썰고, 무순은 물에 잠시 담그어 놓고, 사과는 채를 썰어 설탕물에 잠시 담그어 놓고, 팽이버섯은 밑둥만 잘라 한 줌 집기 좋은 크기로 준비해 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_3.png'),
    (131, 4, '4. 다진마늘과 다진 양파 ,올리브오일, 설탕, 식초, 소금을 넣고, 골고루 섞어 소스를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_4.png'),
    (131, 5, '5. 쪄 낸 단호박은 껍질을 벗기고 채에 곱게 내려 밀가루와 물, 소금을 조금 섞어 단호박 반죽을 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_5.png'),
    (131, 6, '6. 팬에 올리브 오일을 바르고, 만들어 놓은 단호박 반죽을 한 국자씩 떠 넣어, 밀전병을 만들고, 밀전병이 익으면, 한 김 식혀, 그 안에 소고기 볶음과 채소를 넣어 돌돌 말아 소스와 함께 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_6.png'),
    (132, 1, '1. 생선살에 생강즙과 정종을 넣고 약 30분 정도 숙성을 시켜 비릿한 맛 제거 및 생선 살의 탱탱함을 살려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_1.png'),
    (132, 2, '2. 콩가루와 밀가루, 카레가루, 녹말가루, 달걀을 넣고 골고루 섞어 튀김 반죽을 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_2.png'),
    (132, 3, '3. 깻잎은 반으로 접어 채를 썰어 물에 잠시 담그어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_3.png'),
    (132, 4, '4. 깻잎(③)은 물기를 제거하고 튀김반죽(②)을 골고루 입힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_4.png'),
    (132, 5, '5. 숙성시킨 생선살(①)에 녹말가루를 살짝 입히고 튀김 반죽(②)을 골고루 입힌 뒤, 튀김반죽을 입힌 깻잎(④)을 얹어 튀김 기름 170℃ 온도에 넣어 약 2분 정도 튀겨 기름을 자연스럽게 빼 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_5.png'),
    (132, 6, '6. 마요네즈와 요구르트, 설탕, 식초를 넣어 골고루 섞어 튀김소스를 만들고, 튀김을 접시에 담아 양상추와 함께 얹어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_6.png'),
    (133, 1, '1. 썰어 놓은 삼겹살에 생갑즙과 정종을 넣고 약 2시간 정도 숙성을 시켜 준비 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_1.png'),
    (133, 2, '2. 가지는 씨가 적은 것으로 골라 반으로 갈라 어슷썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_2.png'),
    (133, 3, '3. 양파와 깻잎은 채를 썰고, 당근은 직사각형으로 썰고, 대파는 송송 썰고, 느타리는 손으로 찢어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_3.png'),
    (133, 4, '4. 고춧가루과 다진마늘, 간장, 올리고당, 후춧가루, 설탕을 넣고 골고루 섞어 양념장을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_4.png'),
    (133, 5, '5. 숙성시켜 놓은 삼겹살에 만들어 놓은 양념장을 넣고 골고루 무쳐 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_5.png'),
    (133, 6, '6. 팬에 기름을 살짝 둘러 양념한 고기(⑤) 를 먼저 볶은 후 고기가 중간쯤 익으면, 가지(②)와 썰어 놓은 채소(③)를 넣어 볶다가 마지막에 참기름과 잣을 넣고 한 번 더 볶은 후 통깨를 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_6.png'),
    (134, 1, '1. 끓는 물에 식초 한 방울을 떨어 뜨려 주꾸미를 데쳐 한김 식혀 먹기 좋게 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_1.png'),
    (134, 2, '2. 끓는 물에 식초 한 방울을 떨어 뜨려 주꾸미를 데쳐 한김 식혀먹기 좋게 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_2.png'),
    (134, 3, '3. 양상추는 손으로 뜯어, 찬물에 담그어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_3.png'),
    (134, 4, '4. 미나리는 약 4cm 길이로 썰고, 홍고추는 어슷 썰어 씨를 살짝 제거하고 피망은 씨를 제거하고 미나리와 비슷한 길이로 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_4.png'),
    (134, 5, '5. 강판에 곱게 간 배즙과 무즙에 와사비, 설탕, 다진마늘, 식초, 소금을 넣어 샐러드소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_5.png'),
    (134, 6, '6. 만들어 놓은 샐러드소스와 함께 썰어 놓은 주꾸미(②)와 채소(③, ④)를 넣어 먹기 직전에 버무려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_6.png'),
    (135, 1, '1. 닭가슴살에 칼을 1/3 쯤 넣고 양쪽으로 포를 뜨고, 정종을 살짝 발라놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_1.png'),
    (135, 2, '2. 포뜬 닭가슴살 위에 카레가루를 솔솔 뿌려 숙성을 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_2.png'),
    (135, 3, '3. 현미쌀은 물에 충분히 불려, 물기를 빼 놓고, 양배추와 적 양배추는 곱게 채를 썰어 각각 찬물에 담그어 싱싱 해 지면 건져 물기를 빼놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_3.png'),
    (135, 4, '4. 냄비에 토마토소스를 끓이다가 생크림을 넣고 살짝 한 번 더 끓여놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_4.png'),
    (135, 5, '5. 튀김기름에 온도가 약 170℃ 정도가 되면, 불려 놓은 현미쌀의 물기를 제거한 뒤 튀겨 기름을 제거해 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_5.png'),
    (135, 6, '6. 숙성된 닭가슴살(①)에 튀긴 현미쌀 (⑤)을 앞뒤로 골고루 바르고 다시 170℃ 온도의 튀김 기름에 약 3~5분 정도 튀겨내고, 양배추와 적채를 깔고 그 위에 튀겨진 닭가슴살을 올리고, 토마토소스를 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_6.png'),
    (136, 1, '1. 삼겹살에 소금, 후춧가루, 생강즙, 정종을 넣고 약 30분 정도 숙성을 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_1.png'),
    (136, 2, '2. 조와 쌀은 물에 약 30분 정도 불려 밥을 짓고, 밥이 다 익으면, 뚜껑을 열어 주걱 으로 골고루 섞어 한 김을 빼 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_2.png'),
    (136, 3, '3. 파는 길게 채를 썰어 물에 담그어 파채가 둥글게 말리도록 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_3.png'),
    (136, 4, '4. 설탕과 식초, 레몬, 소금, 고춧가루, 매실액을 골고루 섞어 소스를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_4.png'),
    (136, 5, '5. 파프리카는 속을 제거하고 양파, 당근, 파프리카를 양파와 같은 길이로 채를 썰고, 새송이와 표고버섯은 살짝 데쳐 채를 썰고, 깻잎도 채를 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_5.png'),
    (136, 6, '6. 파프리카는 속을 제거하고 양파, 당근, 파프리카를 양파와 같은 길이로 채를 썰고, 새송이와 표고버섯은 살짝 데쳐 채를 썰고, 깻잎도 채를 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_6.png'),
    (137, 1, '1. 미니버섯을 깨끗이 씻어 소금을 살짝 뿌려 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_1.png'),
    (137, 2, '2. 녹말가루에 물을 반쯤 넣고, 가만히 가라앉혀 윗물은 따라 버리고, 딱딱한 된녹말을 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_2.png'),
    (137, 3, '3. 브로컬리는 끓는 물에 데쳐 작게 썰고, 오이는 반달로 썰고, 당근은 은행잎 모양으로 썰어 준비하고, 파프리카는 속씨를 제거하고, 삼각지게 썰어 준비 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_3.png'),
    (137, 4, '4. 간장과 홍초, 설탕을 넣어 골고루 섞어 탕수육소스를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_4.png'),
    (137, 5, '5. 소금을 뿌려 놓은 미니 버섯에 만들어 놓은 된 녹말과 달걀흰자를 넣어 골고 루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_5.png'),
    (137, 6, '6. 튀김 기름의 온도가 170~180℃가 되면, 녹말 입힌 미니버섯을 바삭 하게 튀겨 주고, 다시 팬에 만들어 놓은 탕수육소스를 바글바글 끓이다가, 썰어 놓은 채소를 넣고 살짝 익힌 후, 물녹말을 한 숟가락 정도 넣어 끓인 후 튀겨진 미니버섯 위에 올려 완성해 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_6.png'),
    (138, 1, '1. 연어는 소금, 후춧가루, 레몬으로 마리네이드 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_1.png'),
    (138, 2, '2. 양파, 마늘은 입자있게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_2.png'),
    (138, 3, '3. 토마토는 씨를 제거하고 입자있게 썰고, 파프리카, 피망은 채 썬 뒤 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_3.png'),
    (138, 4, '4. 화이트크림, 생크림을 골고루 섞어 크림소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_4.png'),
    (138, 5, '5. 팬에 버터를 넣고 양파, 마늘을 볶다가 만들어 놓은 크림소스를 넣고 졸인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_5.png'),
    (138, 6, '6. 접시에 볶은 야채를 담고, 200℃ 오븐에서 10분 정도 구운 연어를 담은 뒤 크림소스를 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_6.png'),
    (139, 1, '1. 쌀은 깨끗이 씻어 30분 이상 불려 밥을 지어 식힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_1.png'),
    (139, 2, '2. 옥수수콘은 체에 걸러 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_2.png'),
    (139, 3, '3. 양파, 애호박, 당근, 양파 및 새송이버섯은 옥수수콘 크기로 썰고 마늘은 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_3.png'),
    (139, 4, '4. 팬에 기름을 두르고 야채를 먼저 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_4.png'),
    (139, 5, '5. 볶은 야채에 식혀놓은 밥을 넣고 볶다가 저염간장, 후춧가루로 간을 하고 옥수수콘을 넣고 살짝 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_5.png'),
    (139, 6, '6. 팬에 기름을 자작하게 넣고, 라이스페이퍼를 둥글게 넣고 튀겨 그릇에 담은 뒤 볶음밥을 담는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_6.png'),
    (140, 1, '1. 연어는 끝을 뾰족하게 살려 슬라이스한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_1.png'),
    (140, 2, '2. 쌀은 깨끗이 씻어 30분 이상 불려 밥을 지어 식힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_2.png'),
    (140, 3, '3. 냄비에 식초, 설탕, 소금 약간과 물을 동량으로 넣고 끓인 뒤 식혀 촛물을 만들어 밥에 넣고 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_3.png'),
    (140, 4, '4. 당근과 표고버섯은 입자있게 다져 살짝 볶아 식혀놓은 초밥에 넣고 잘 섞어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_4.png'),
    (140, 5, '5. 와사비 가루에 30℃정도의 물을 넣고 섞은 뒤 발효 시키고, 초밥 형태를 만들어 그 위에 와사비를 바르고 연어를 올린 후 접시에 담는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_5.png'),
    (140, 6, '6. 새싹과 파슬리는 찬물에 담았다 건져 물기를 빼고, 연어 초밥 옆에 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_6.png'),
    (141, 1, '1. 찹쌀가루는 체에 내려 2등분한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00574_1.png'),
    (141, 2, '2. 찹쌀가루는 오미자와 치자를 우린 뜨거운 물로 익반죽하여 비닐봉투에 담아 숙성시킨다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00574_2.png'),
    (141, 3, '3. 대추는 씨를 발라내 곱게 다지고 귤병, 유자청, 꿀을 섞어 원소병 소를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00574_3.png'),
    (141, 4, '4. 굴병과 꿀을 섞어 원소병 소를 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00574_4.png'),
    (141, 5, '5. 익반죽을 지름 3cm 크기로 동그랗게 만들고 안에 소를 넣어 다시 동그랗게 만든다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00574_5.png'),
    (141, 6, '6. 녹말가루를 입혀 끓는 물에 넣은 뒤 떠오르면 건져 찬물에 헹궈 물기를 빼고 그릇에 담아 꿀물을 붓고 잣을 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00574_6.png'),
    (142, 1, '1. 소고기는 넓게 펴서 소금, 후춧가루로 밑간을 하고 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_1.png'),
    (142, 2, '2. 닭가슴살은 소금, 후춧가루로 밑간을 하여 굽고 길이대로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_2.png'),
    (142, 3, '3. 미나리는 끓는 물에 넣고 데쳐 찬물에 헹군다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_3.png'),
    (142, 4, '4. 홍고추는 씨를 제거 하고 길게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_4.png'),
    (142, 5, '5. 달걀은 황백으로 나눠 지단을 부쳐 길게 썰어 놓는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_5.png'),
    (142, 6, '6. 밑간한 소고기와 닭살 에 찹쌀가루를 뭍혀 홍고추와 달걀 황백지단을 넣어 돌돌 말아 미나리로 띠를 둘러 팬에 기름을 살짝 둘러 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_6.png'),
    (143, 1, '1. 쌀은 깨끗이 씻어 30분 정도 불린다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_1.png'),
    (143, 2, '2. 전복은 수저로 떼어 내어 소금으로 세척한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_2.png'),
    (143, 3, '3. 준비된 전복에 버터를 두르고 굽는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_3.png'),
    (143, 4, '4. 당근. 양파 . 양송이는 입자 있게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_4.png'),
    (143, 5, '5. 냄비에 올리브 오일을 넣고 불린 쌀을 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_5.png'),
    (143, 6, '6. 쌀이 어느 정도 볶아지면 다져 놓은 야채를 넣어 볶다가 우유를 넣어 끓이고, 쌀이 퍼지면 구워 놓은 전복을 넣어 다시 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_6.png'),
    (144, 1, '1. 참깨와 흑임자를 섞어 곱게 간다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_1.png'),
    (144, 2, '2. 갈아 놓은 참깨와 흑임자에 설탕과 식초를 넣고 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_2.png'),
    (144, 3, '3. 2번에 우유와 마요네즈를 넣고 골고루 섞어 참깨 흑임자 드레싱을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_3.png'),
    (144, 4, '4. 연두부는 동그랗게 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_4.png'),
    (144, 5, '5. 파프리카는 채 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_5.png'),
    (144, 6, '6. 접시에 드레싱, 연두부, 파프리카, 무순, 날치알 순으로 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_6.png'),
    (145, 1, '1. 냄비에 마늘과 대파를 넣고 끓이다가 닭다리를 넣고 삶는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_1.png'),
    (145, 2, '2. 닭을 삶아 놓은 육수는 걸러 기름을 제거 하고 차갑게 식힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_2.png'),
    (145, 3, '3. 삶아 놓은 닭고기는 결대로 찢는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_3.png'),
    (145, 4, '4. 양파, 오이와 파프리카는 채썬다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_4.png'),
    (145, 5, '5. 적채는 채를 썰어 찬물에 담궈 건져 물기를 빼 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_5.png'),
    (145, 6, '6. 차갑게 걸러 놓은 닭 육수에 발효된 겨자와 소금 .설탕, 식초를 넣어 육수 간을 하고, 그릇에 닭살과 오이 적채, 파프리카 양파를 올려 차가운 육수를 넣어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_6.png'),
    (146, 1, '1. 감자는 채를 썰어 물에 담궈 전분을 제거하고, 양파와 당근 파는 채를 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_1.png'),
    (146, 2, '2. 콩나물은 머리와 꼬리를 손질한다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_2.png'),
    (146, 3, '3. 물이 끓으면 손질한 콩나물을 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_3.png'),
    (146, 4, '4. 콩나물이 익으면 체에 건져 찬물에 헹군다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_4.png'),
    (146, 5, '5. 부추는 약 6cm 길이로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_5.png'),
    (146, 6, '6. 팬에 기름을 두르고 콩나물을 넣고 살짝 볶다가 썰어 놓은 야채를 넣고 볶으면서 소금 간을 하고 마지막에 부추를 넣고', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_6.png'),
    (147, 1, '1. 비지는 살짝 데쳐 물기를 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_1.png'),
    (147, 2, '2. 무장아찌는 잘게 다져 비지에 골고루 섞어 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_2.png'),
    (147, 3, '3. 토마토는 슬라이스로 썰어 팬에 오일을 두르고 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_3.png'),
    (147, 4, '4. 포카치아빵은 반으로 갈라 비지소스를 바른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_4.png'),
    (147, 5, '5. 생치즈는 동그랗게 원형으로 찍어, 비지를 바른 빵에 구운 토마토와 함께 올리고, 다시 그 위에 비지를 올려 접시에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_5.png'),
    (147, 6, '6. 루꼴라는 찬물에 담궈 건져 포카치아빵 옆에 함께 놓는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_6.png'),
    (148, 1, '1. 소고기는 후춧가루, 올리브오일로 밑간을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_1.png'),
    (148, 2, '2. 더덕은 칼로 껍질을 돌려 깎고, 파프리카, 아스파라거스와 함께 가늘게 채썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_2.png'),
    (148, 3, '3. 파인애플, 양파, 당근, 대파는 채썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_3.png'),
    (148, 4, '4. 밑간한 고기를 팬에 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_4.png'),
    (148, 5, '5. 따로 갈아 놓은 파인애플과 레몬즙 꿀을 섞어 드레싱을 만는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_5.png'),
    (148, 6, '6. 구워 놓은 고기에 더덕과 파인애플 아스파라거스 깻잎을 넣어 돌돌 말고, 팬에 발사믹 소스를 끓인 뒤 말아 놓은 소고기롤을 넣어 졸인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_6.png'),
    (149, 1, '1. 새우는 껍질을 벗겨 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_1.png'),
    (149, 2, '2. 오징어는 썰어서 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_2.png'),
    (149, 3, '3. 양파, 샐러리, 마늘은 다져 팬에 볶아 식힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_3.png'),
    (149, 4, '4. 토마토는 껍질을 벗겨 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_4.png'),
    (149, 5, '5. 다진 토마토를 팬에 볶다가 다진 새우와 오징어, 밥을 넣어 볶고, 3번을 넣고 볶아 동그랗게 모양을 만든다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_5.png'),
    (149, 6, '6. 동그란 밥에 레몬을 섞은 달걀노른자를 묻히고 빵가루 옷을 입혀 팬에 기름을 둘러 굴려가며 익힌 후 접시에 담고 어린잎을 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_6.png'),
    (150, 1, '1. 닭고기는 넓게 펴 소금, 후추로 밑간을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_1.png'),
    (150, 2, '2. 찹쌀은 깨끗이 씻어 밥을 짓는다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_2.png'),
    (150, 3, '3. 인삼은 뇌두를 제거 하고 깨끗이 씻어 길게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_3.png'),
    (150, 4, '4. 미나리는 데쳐 찬물에 헹구고, 대추는 씨를 제거하고 채를 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_4.png'),
    (150, 5, '5. 호박잎은 끓는 물에 데쳐 찬물에 식힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_5.png'),
    (150, 6, '6. 데친 호박잎에 밑간한 닭고기와 찹쌀밥을 올리고 그 위에 인삼, 데친 미나리, 대추를 올려 돌돌 말아 찜통에 찐 뒤 먹기 좋게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_6.png'),
    (151, 1, '1. 소고기는 홍두깨살로 준비해서 0.3mm 두께로 썰고, 배는 껍질을 벗겨 채 썰어 설탕을 뿌린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_1.png'),
    (151, 2, '2. 끓는 물에 소금을 넣고 두릅을 데친 후 찬물에 헹구어 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_2.png'),
    (151, 3, '3. 소금과 설탕, 다진 마늘을 넣고 소고기 육회 양념장을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_3.png'),
    (151, 4, '4. 삶은 달걀노른자와 크림치즈, 레몬을 섞어 짤주머니에 담는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_4.png'),
    (151, 5, '5. 숙성이 잘된 아보카도는 강판에 갈아 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_5.png'),
    (151, 6, '6. 접시에 양념한 육회와 두릅을 담고 그 위에 배를 올리고, 달걀크림과 아보카도를 짜서 올린다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_6.png'),
    (152, 1, '1. 닭은 깨끗이 씻고 포를 뜬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_1.png'),
    (152, 2, '2. 닭고기는 15분 정도 삶아 식으면 곱게 다져 다진 마늘, 대파, 저염간장을 넣고 완자를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_2.png'),
    (152, 3, '3. 인삼은 뇌두를 제거하고 깨끗이 씻는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_3.png'),
    (152, 4, '4. 찹쌀은 약 30분 정도 충분히 불린다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_4.png'),
    (152, 5, '5. 대추는 소금물에 깨끗이 씻고 씨를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_5.png'),
    (152, 6, '6. 믹서에 인삼과 대추, 불린 찹쌀, 물을 정도 넣고 곱게 간 뒤, 냄비에 담아 죽을 쑤다가 다 익으면 만들어 놓은 완자를 넣고 한소끔 더 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_6.png'),
    (153, 1, '1. 새우는 내장을 제거하고 껍질을 벗겨 깨끗이 씻는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309015216_1678337536871.jpg'),
    (153, 2, '2. 찜기에 새우와 생강을 넣어 약 5분 정도 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021346_1678338826806.jpg'),
    (153, 3, '3. 마늘은 납작 썰고, 당근과 양파, 파, 깻잎, 청고추, 대추는 굵게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021409_1678338849298.jpg'),
    (153, 4, '4. 숙주는 꼬리를 떼어 다듬고, 끓는 물에 살짝 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021426_1678338866582.jpg'),
    (153, 5, '5. 팬에 식용유를 두른 뒤 손질한 채소를 넣어 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021447_1678338887494.jpg'),
    (153, 6, '6. 밥과 새우를 넣어 볶고, 저염간장을 넣어 볶아 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021505_1678338905796.jpg'),
    (154, 1, '1. 토마토는 깨끗이 씻어 슬라이스한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_1.png'),
    (154, 2, '2. 두부는 원형틀을 이용해서 토마토와 같은 크기로 만들고 소금을 살짝 뿌린 뒤 물기를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_2.png'),
    (154, 3, '3. 팬에 올리브오일을 두르고 두부를 살짝 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_3.png'),
    (154, 4, '4. 어린잎은 찬물에 담근다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_4.png'),
    (154, 5, '5. 발사믹소스에 설탕과 레몬즙을 넣고 골고루 섞어 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_5.png'),
    (154, 6, '6. 두부와 준비한 토마토를 접시에 돌려 담고 어린잎을 위에 올린 뒤 먹기 직전에 발사믹소스와 파마산치즈를 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_6.png'),
    (155, 1, '1. 쌀과 다시마를 넣고 쌀과 동량의 물을 부어 밥을 고슬고슬하게 짓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_1.png'),
    (155, 2, '2. 양파는 굵게 다지고, 쪽파와 고추는 송송 썰고, 오징어와 주꾸미는 먹기 좋은 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_2.png'),
    (155, 3, '3. 달군 팬에 들기름을 두르고 다진 마늘을 볶아 향을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_3.png'),
    (155, 4, '4. 미역을 넣고 볶다가 손질해둔 오징어, 주꾸미, 새우살, 양파를 넣어 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_4.png'),
    (155, 5, '5. 밥을 넣고 소금을 넣어 볶다가 들깻가루를 넣어 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_5.png'),
    (155, 6, '6. 송송 썬 쪽파와 청양고추, 붉은 고추를 넣어 조금 더 볶아 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_6.png'),
    (156, 1, '1. 쌀과 현미는 물에 깨끗이 씻어 불린 후 밥을 고슬고슬하게 짓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_1.png'),
    (156, 2, '2. 감자, 고구마, 우엉, 양파, 당근, 애호박, 연근은 작게 깍둑 썰고, 마늘은 편 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_2.png'),
    (156, 3, '3. 시금치와 참나물, 깻잎은 굵게 채 썰고, 미나리는 2.5㎝ 길이로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_3.png'),
    (156, 4, '4. 물(50g)에 다시마를 우려내 육수를 내고 청국장을 넣어 잘 푼 뒤 들깻가루를 넣고 잘 저어주며 졸여 청국장소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_4.png'),
    (156, 5, '5. 팬에 올리브유와 들기름을 두르고 양파, 마늘을 약한 불에 볶다가 감자, 고구마, 우엉, 연근, 당근, 애호박을 넣고 같이 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_5.png'),
    (156, 6, '6. 밥과 청국장소스를 부어 80% 정도 볶다가 시금치, 참나물, 미나리를 넣고 한 번 더 볶고 채 썬 깻잎을 올려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_6.png'),
    (157, 1, '1. 쌀은 씻어서 체에 건져 1시간 이상 불린 후 동량의 물을 부어 밥을 짓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_1.png'),
    (157, 2, '2. 아스파라거스, 새송이버섯은 작게 깍둑 썰고, 무와 우엉, 미나리는 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_2.png'),
    (157, 3, '3. 식용유(10g)를 두른 팬을 중간 불로 달궈 달걀을 넣고 스크램블로 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_3.png'),
    (157, 4, '4. 문어, 새우살, 주꾸미는 끓는 물에 살짝 데쳐 작게 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_4.png'),
    (157, 5, '5. 식용유(15g)를 두른 팬을 중간 불로 달궈 밥을 넣어 볶다가 문어, 새우살, 주꾸미, 스크램블 에그를 넣고 조금 더 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_5.png'),
    (157, 6, '6. 무와 우엉에 고추장과 참기름, 물(40g)을 넣고 묽게 졸인 후 불을 끄고 미나리를 넣고 섞어 미나리볶음고추장을 만들어 밥에 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_6.png'),
    (158, 1, '1. 감자, 양파, 당근, 피망은 볶음밥에 넣을 분량은 잘게 다지고, 카레에 넣을 분량은 한입 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_1.png'),
    (158, 2, '2. 닭가슴살도 한입 크기로 잘라 후춧가루로 밑간한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_2.png'),
    (158, 3, '3. 팬에 식용유(15g)를 두른 뒤 잘게 다진 감자, 양파, 당근, 피망을 넣고 볶다가 밥을 넣고 같이 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_3.png'),
    (158, 4, '4. 식용유(15g)를 두른 팬에 닭고기를 먼저 볶다가 한입 크기로 썬 감자, 당근, 양파, 피망, 브로콜리를 넣고 볶은 뒤 마지막에 방울토마토를 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_4.png'),
    (158, 5, '5. 토마토페이스트을 넣고 볶다가 파프리카가루를 넣어 향을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_5.png'),
    (158, 6, '6. 카레가루를 푼 물(300g)을 부어 끓이다가 플레인 요구르트를 넣고 농도를 맞춘 뒤 밥 위에 얹어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_6.png'),
    (159, 1, '1. 현미로 밥을 지은 뒤 밥 밑간을 넣고 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_1.png'),
    (159, 2, '2. 톳에 톳 무침양념을 넣고 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_2.png'),
    (159, 3, '3. 밥에 톳을 넣고 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_3.png'),
    (159, 4, '4. 밥을 먹기 좋은 크기로 둥글게 뭉친 뒤 부순 생김을 골고루 묻힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_4.png'),
    (159, 5, '5. 주먹밥에 달걀물을 입힌 뒤 팬에 구워 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_5.png'),
    (160, 1, '1. 칵테일새우는 잘게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_1.png'),
    (160, 2, '2. 다진 쇠고기에 후춧가루와 참기름(3g)으로 밑간한 후 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_2.png'),
    (160, 3, '3. 찹쌀현미밥에 참기름(4g)을 넣어 밑간하고 식힌 후 새우와 쇠고기를 넣어 뭉쳐준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_3.png'),
    (160, 4, '4. 마늘과 고추는 볶다가 식초와 함초가루, 설탕, 물(30g)을 넣고 졸여 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_4.png'),
    (160, 5, '5. 밀가루는 반죽한 뒤 얇게 밀어 삼각형으로 잘라 튀긴 후 기름을 뺀다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_5.png'),
    (160, 6, '6. 위에 주먹밥을 올리고 파인애플을 올린 후 소스를 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_6.png'),
    (161, 1, '1. 닭봉은 손질한 뒤 후춧가루를 섞은 우유에 담가두었다가 구워 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_1.png'),
    (161, 2, '2. 단호박, 새송이버섯은 구워서 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_2.png'),
    (161, 3, '3. 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_3.png'),
    (161, 4, '4. 가지, 주키니호박, 당근, 양파, 시래기는 잘게 다진 뒤 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_4.png'),
    (161, 5, '5. 볶은 채소와 치즈를 참기름, 간장과 함께 밥에 넣고 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_5.png'),
    (161, 6, '6. 구운 닭봉을 밥으로 감싼 뒤 구운 단호박, 새송이버섯, 새싹채소와 함께 담아 소스를 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_6.png'),
    (162, 1, '1. 봄동과 깻잎은 데친 뒤 체에 밭쳐 물기를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_1.png'),
    (162, 2, '2. 데친 꼬막살은 물기를 제거하고 다져 올리브유로 양념한 밥에 참깨와 함께 버무린 후 한입 크기로 주먹밥을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_2.png'),
    (162, 3, '3. 낙지 다리는 데쳐 찬물에 담가 식힌 뒤 참기름(3g)에 양념하고, 두릅은 데쳐 물기를 제거한 뒤 올리브유로 양념한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_3.png'),
    (162, 4, '4. 봄동에 꼬막밥을 넣고 만 뒤 깻잎으로 싸고 데친 미나리로 묶어 봄주먹밥을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_4.png'),
    (162, 5, '5. 냄비에 참기름(10g)을 두르고 양파, 당근, 애호박을 넣고 볶은 뒤 물, 된장, 고춧가루, 다진 마늘, 붉은 고추, 청양고추를 넣고 끓이다가 두부를 넣고 조려 두부강된장을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_5.png'),
    (162, 6, '6. 봄주먹밥에 낙지와 두릅, 두부강된장을 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_6.png'),
    (163, 1, '1. 쌀을 씻어 밥을 지은 뒤 밥 밑간을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_1.png'),
    (163, 2, '2. 양파, 피클, 삶은 달걀은 다진 뒤 마요네즈에 섞어 허브타르타르드레싱을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_2.png'),
    (163, 3, '3. 김 위에 양쪽으로 밥을 펴고 허브타르타르드레싱을 위에 올린 후 만다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_3.png'),
    (163, 4, '4. 깻잎장아찌를 바닥에 깔고 말아놓은 롤을 올린 후 한 번 더 만다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_4.png'),
    (163, 5, '5. 머스타드소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_5.png'),
    (163, 6, '6. 한입 크기로 롤을 썬 뒤 새싹과 소스를 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_6.png'),
    (164, 1, '1. 쌀을 물에 불린 뒤 완두콩을 함께 넣고 밥을 짓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_1.png'),
    (164, 2, '2. 달걀을 풀어 체에 내린 뒤 지단을 부치면서 반 정도 익었을 때 그 위에 얇게 썬 주키니호박, 당근, 가지를 올려 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_2.png'),
    (164, 3, '3. 참치를 참치밑간에 재운 뒤 겉만 살짝 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_3.png'),
    (164, 4, '4. 김에 밥을 올리고, 뒤집어 참치를 올려 만 뒤 그 위에 지단을 올려 한 번 더 만다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_4.png'),
    (164, 5, '5. 사과드레싱을 만들고, 토마토소스 재료를 간 뒤 체에 거른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_5.png'),
    (164, 6, '6. 참치비빔밥롤을 담은 뒤 토마토소스를 곁들이고, 케일에 사과드레싱을 뿌려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_6.png'),
    (165, 1, '1. 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_1.png'),
    (165, 2, '2. 파프리카와 파인애플을 먹기 좋게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_2.png'),
    (165, 3, '3. 스크램블 에그를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_3.png'),
    (165, 4, '4. 김발 위에 ⅔크기로 자른 김을 놓고 밥을 고루 편 뒤 새싹채소, 스크램블 에그, 파프리카, 기름 뺀 참치, 파인애플을 올리고 소스를 고르게 뿌린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_4.png'),
    (165, 5, '5. 반으로 접어 먹기 좋게 썰어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_5.png'),
    (166, 1, '1. 당근, 피망, 양파를 잘게 다진 뒤 기름에 살짝 볶아 밥에 마요네즈(10g)와 함께 넣어 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_1.png'),
    (166, 2, '2. 통조림 연어는 기름기를 빼고, 식초를 넣은 끓는 물에서 30초 정도 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_2.png'),
    (166, 3, '3. 데리야키소스와 마요네즈(10g)에 연어를 넣고 버무려 양념한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_3.png'),
    (166, 4, '4. 밥을 동그랗게 빚은 뒤 가운데에 연어를 넣고 주먹밥을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_4.png'),
    (166, 5, '5. 달걀물과 빵가루를 입혀 160℃로 예열한 기름에 바삭하게 튀긴다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_5.png'),
    (166, 6, '6. 연어주먹밥튀김을 담고 어린잎채소를 곁들인 뒤 오리엔탈소스를 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_6.png'),
    (167, 1, '1. 오징어 몸통 안쪽에 칼집을 내고 끓는 물에 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_1.png'),
    (167, 2, '2. 파프리카와 당근, 피망은 작게 다져 볶은 뒤 밥에 넣어 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_2.png'),
    (167, 3, '3. 쇠고기는 참기름에 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_3.png'),
    (167, 4, '4. 달걀을 풀어 약한 불로 달군 팬에 부은 뒤 김가루를 뿌려 지단을 부치고, 바닥이 익으면 깻잎과 밥을 올린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_4.png'),
    (167, 5, '5. 데친 오징어, 볶은 쇠고기 순으로 올려 만 뒤 랩으로 싼다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_5.png'),
    (167, 6, '6. 고추장과 매실청을 섞은 뒤 팬에 넣고 약한 불에서 끓여 소스를 만들고 한입 크기로 썬 김밥에 구운 버섯을 함께 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_6.png'),
    (168, 1, '1. 닭고기 삶는 재료를 물(100g)에 넣어 끓이다가 닭가슴살을 넣고 삶아 한입 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_1.png'),
    (168, 2, '2. 양배추는 곱게 채 썰고, 청포묵은 먹기 좋은 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_2.png'),
    (168, 3, '3. 숙주는 끓는 물에 데쳐 물기를 뺀다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_3.png'),
    (168, 4, '4. 비빔소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_4.png'),
    (168, 5, '5. 쌀에 물을 붓고 밥을 짓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_5.png'),
    (168, 6, '6. 밥 위에 닭가슴살, 양배추, 청포묵, 숙주, 달걀지단, 무순을 담아 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_6.png'),
    (169, 1, '1. 조미된 족발은 뜨거운 맥주에 데쳐내 기름기를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_01.png'),
    (169, 2, '2. 고구마는 편으로 썰어 냉수에 헹궈 기름 없는 팬에 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_02.png'),
    (169, 3, '3. 가지는 편으로 썰어서 수분을 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_03.png'),
    (169, 4, '4. 가지는 편으로 썰어 살짝 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_04.png'),
    (169, 5, '5. 백김치는 물에 헹궈서 손으로 꼭 짠 후 송송 썰어서 양념을 해 무쳐준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_05.png'),
    (169, 6, '6. 무쳐준 김치와 함께 구워진 채소와 족발수육을 담아 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00325_1.png'),
    (170, 1, '1. 함초는 송송 다져준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_01.png'),
    (170, 2, '2. 당근, 새송이, 샐러리, 양파를 송송 다져준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_02.png'),
    (170, 3, '3. 볼에 소고기, 돼지고기, 다져 놓은 채소와 다진마늘, 다진대파, 참기름을 넣고 반죽해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_03.png'),
    (170, 4, '4. 여기에 함초 다진 것을 넣고 다시 반죽해 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_04.png'),
    (170, 5, '5. 떡갈비 모양으로 만들어준 후 팬에 노릇하게 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_05.png'),
    (170, 6, '6. 소스를 만들어 구워진 떡갈비를 졸여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_06.png'),
    (171, 1, '1. 모시조개는 볶은소금을 넣어서 해감시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_01.png'),
    (171, 2, '2. 준비된 다시마물에 준비된 채소의 국물재료를 넣고 국물을 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_02.png'),
    (171, 3, '3. 만들어진 국물에 모시조개를 넣고 청주를 부어서 국물을 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_03.png'),
    (171, 4, '4. 쪽파는 송송 다지고 마늘은 곱게 다져주고 청홍고추는 가늘게 채 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_04.png'),
    (171, 5, '5. 면을 각각 삶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_05.png'),
    (171, 6, '6. 삼색 면을 그릇에 담고 만들어진 국물을 부워서 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_06.png'),
    (172, 1, '1. 당근, 청경채, 배추, 애호박, 팽이버섯은 먹기 좋은 크기로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_01.png'),
    (172, 2, '2. 흑돼지고기, 저염된장, 후추를 넣어서 불순물을 제거하면서 고기를 삶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_02.png'),
    (172, 3, '3. 천연다시다로 육수를 만들어 체에 걸러준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_03.png'),
    (172, 4, '4. 손질한 야채들과 청양고추를 잘라 함께 육수에 데쳐 국물에 매운 맛을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_04.png'),
    (172, 5, '5. 곁들이장을 만들어 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_05.png'),
    (172, 6, '6. 냄비에 데쳐준 채소와 국물을 붓고 고기를 먹기좋게 썰어 소스랑 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_06.png'),
    (173, 1, '1. 바지락은 해감을 시켜준 후 물에 넣어 바지락 육수를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_01.png'),
    (173, 2, '2. 바지락살은 따로 떼어내어 다진소고기와 다시 다져서 양념해 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_02.png'),
    (173, 3, '3. 두부는 물기제거를 한 후 함초, 깻잎, 다진양파, 를 넣어 치대어 두부볶음을 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_03.png'),
    (173, 4, '4. 애호박은 채 썰어주고 양파, 오이, 당근, 표고도 채 썰어 각각 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_04.png'),
    (173, 5, '5. 볶은 바지락과 쌀을 넣어 밥을 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_05.png'),
    (173, 6, '6. 몰드에 밥과 각종 채소들을 올려주고 마지막에 두부 스크램블을 올려 밥을 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_06.png'),
    (174, 1, '1. 뜨거운 물에 파스타면을 삶아낸 뒤 물기를 빼준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_01.png'),
    (174, 2, '2. 송송 썬 청양고추를 생들기름에 볶아주다 면을 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_02.png'),
    (174, 3, '3. 볶아준 청양고추에 삶아진 면을 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_03.png'),
    (174, 4, '4. 열무김치는 송송 썰어서 들기름에 살짝 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_04.png'),
    (174, 5, '5. 오이는 돌려깎기 해서 채 썰어주고 청고추, 2가지색 파프리카, 당근도 채 썰어 각각 볶아 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_05.png'),
    (174, 6, '6. 청양고추에 볶아진 파스타면을 볶은 채소와 한 번 더 볶아 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_06.png'),
    (175, 1, '1. 갈비는 핏물을 빼주고 뜨거운 물에 80% 이상 데쳐준 뒤 갈비와 감자를 물을 자작하게 넣고 청주, 생강청을 넣고 푹 삶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_01.png'),
    (175, 2, '2. 양파, 청고추, 홍고추는 먹기 좋게 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_02.png'),
    (175, 3, '3. 마른미역줄기는 냉수에 행군 뒤 물기를 빼준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_03.png'),
    (175, 4, '4. 양념장을 바글바글 끓여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_04.png'),
    (175, 5, '5. 끓고 있는 양념장에 삶아진 갈비, 감자, 당근 순으로 끓이다 메추리알을 넣고 졸여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_05.png'),
    (175, 6, '6. 마지막에 미역줄기를 넣고 볶아주는 형식으로 갈비찜을 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_06.png'),
    (176, 1, '1. 곤드레는 뜨거운 물에 데쳐낸 뒤 깨끗하게 헹구고 송송 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_01.png'),
    (176, 2, '2. 두부의 물기를 짜 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_02.png'),
    (176, 3, '3. 양념장 재료와 물기를 짠 두부로 양념장을 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_03.png'),
    (176, 4, '4. 느타리버섯은 뜨거운 물에 데쳐낸 후 수분을 빼 가늘게 찢어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_04.png'),
    (176, 5, '5. 냄비에 참기름을 두르고 곤드레를 볶다가 쌀을 넣고 밥을 해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_05.png'),
    (176, 6, '6. 뜸을 들일 때 느타리버섯을 올려주어 밥을 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_06.png'),
    (177, 1, '1. 찹쌀은 불려주고 전복은 손질해 내장과 분리해 준 뒤 청주를 넣어서 전복 내장을 믹서에 갈아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_01.png'),
    (177, 2, '2. 전복 몸에 칼집을 내준 후 참기름을 두른 팬에 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_02.png'),
    (177, 3, '3. 당근과 미역은 송송 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_03.png'),
    (177, 4, '4. 전복내장을 먼저 참기름에 충분히 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_04.png'),
    (177, 5, '5. 내장을 볶아주다가 불린 찹쌀을 넣고 충분히 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_05.png'),
    (177, 6, '6. 죽이 완성될 때 당근, 미역을 넣고 죽을 끓여 주고 전복은 고명으로 올려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_06.png'),
    (178, 1, '1. 주꾸미는 세척해 물기를 빼준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_01.png'),
    (178, 2, '2. 무와 단호박은 먹기 좋게 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_02.png'),
    (178, 3, '3. 청고추, 미나리, 양파를 먹기 좋게 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_03.png'),
    (178, 4, '4. 청양 고추를 어슷하게 썰고 마늘은 편으로 썰어 국물을 끓여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_04.png'),
    (178, 5, '5. 국물이 만들어지면 무, 단호박을 넣고 끓여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_05.png'),
    (178, 6, '6. 국물에 대파를 넣어주고 모든 재료를 넣어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_06.png'),
    (179, 1, '1. 파프라카, 양파, 홍고추는 가늘게 채 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_01.png'),
    (179, 2, '2. 함초는 세척 후 먹기 좋게 손질해 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_02.png'),
    (179, 3, '3. 무순을 물 속에 담가준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_03.png'),
    (179, 4, '4. 편으로 썰어준 소고기에 채 썰어준 채소와 함초를 꼭 싸 말아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_04.png'),
    (179, 5, '5. 마늘은 편으 로 썰 어 마늘기름에 한 번 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_05.png'),
    (179, 6, '6. 구운 마늘을 깔고 말아준 고기를 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_06.png'),
    (180, 1, '1. 닭가슴살을 이용해 국물을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_01.png'),
    (180, 2, '2. 닭가슴살은 먹기 좋게 찢어 흰 후추, 버섯마늘 소금으로 밑간을 해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_02.png'),
    (180, 3, '3. 콩나물은 머리를 떼어내고 데쳐준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_03.png'),
    (180, 4, '4. 계란은 황백지단으로 부친 후 채 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_04.png'),
    (180, 5, '5. 3종류의 묵은 먹기 좋은 크기로 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_05.png'),
    (180, 6, '6. 그릇에 밥을 담고 콩나물을 올리고 3종류의 묵을 올린 후 계란으로 고명해 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_06.png'),
    (181, 1, '1. 쌀을 깨끗하게 씻은 후에 쌀뜨물은 따로 보관하고 불려준 쌀로 밥을 해주고 접시에 펼쳐 식혀준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_01.png'),
    (181, 2, '2. 우엉과 연근, 감자는 송송 다져준 후 아린 맛을 빼기위해 물에 담가준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_02.png'),
    (181, 3, '3. 우엉과 연근, 감자는 물기를 빼고 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_03.png'),
    (181, 4, '4. 청고추, 양파, 당근은 송송 다져서 함께 볶아준 뒤, 함초 소금을 이용해 1차 간을 해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_04.png'),
    (181, 5, '5. 그 릇 에 모 든 재 료 를 넣 고 오 니 기 리 를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_05.png'),
    (181, 6, '6. 그릇에 담아 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_06.png'),
    (182, 1, '1. 단 호박은 속을 파 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_01.png'),
    (182, 2, '2. 찹쌀은 물에 불려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_02.png'),
    (182, 3, '3. 밤은 먹기 좋게 잘라서 물에 담가준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_03.png'),
    (182, 4, '4. 대추는 씨를 제거하고 먹기 좋게 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_04.png'),
    (182, 5, '5. 양념장을 만들어 먼저 쌀과 밤을 재워준 후 찰밥을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_05.png'),
    (182, 6, '6. 단호박 속에 찰밥을 넣어서 찜기에 쪄준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_06.png'),
    (183, 1, '1. 망고는 손질 후 버터로 살짝 볶고 레드와인을 넣고 졸이다가 믹서로 갈아주고 딸기잼으로 간과 농도를 맞추어 레드와인 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_01.png'),
    (183, 2, '2. 소 채끝살은 손질 후 만들어진 레드와인 소스에 마리네이드 해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_02.png'),
    (183, 3, '3. 감자는 껍질을 제거 후 먹기 좋은 크기로 잘라서 뜨거운 물에 데쳐내 버터에 볶아준다. 당근은 먹기좋은 크기로 잘라 뜨거운 물에 데쳐 버터 에 볶아주고, 시금치도 데친 후 수분을 제거하 고 버터에 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_03.png'),
    (183, 4, '4. 마리네이드 한 고기를 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_04.png'),
    (183, 5, '5. 마리네이드 하고 남은 망고소스를 다시 팬에 졸여 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_05.png'),
    (183, 6, '6. 접시에 준비된 채소와 안심을 담고 소스를 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_06.png'),
    (184, 1, '1. 식빵은 가장 자리를 잘라 밀대를 이용 하여 얇게 밀어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_1.png'),
    (184, 2, '2. 감자는 삶은 뒤 곱게 으깨어 올리고당을 넣고 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_2.png'),
    (184, 3, '3. 달걀은 약 13분 정도 삶아 껍질을 벗기고, 반으로 갈라 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_3.png'),
    (184, 4, '4. 당근과 양파 오이도 얇게 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_4.png'),
    (184, 5, '5. 토마토는 포를 떠서 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_5.png'),
    (184, 6, '6. 으깬 감자(②)에 준비한 달걀(③)과 채소(④)를 넣고 소금을 약간 넣은 뒤 골고루 섞어, 밀어 놓은 식빵에 토마토, 치즈, 양상추와 함께 넣어 돌돌 말아 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_6.png'),
    (185, 1, '1. 황태는 가위로 약 3cm 길이로 잘라 스프레이로 물기를 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_1.png'),
    (185, 2, '2. 밀가루를 세 군데로 나눠 각각 찹쌀 가루와 녹말가루, 삼색 가루를 넣어 세 가지의 색을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_2.png'),
    (185, 3, '3. 만들어 놓은 밀가루에 달걀을 넣어 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_3.png'),
    (185, 4, '4. 튀김 반죽(③)에 황태(①)를 넣어 섞은 후, 튀김 기름 170℃ 의 온도에서 약 1분 정도 튀겨 기름을 빼 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_4.png'),
    (185, 5, '5. 파프리카는 속씨를 제거하고, 양파와 당근, 오이, 파인애플과 함께 썰어 준비 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_5.png'),
    (185, 6, '6. 저염간장에 설탕과 식초를 넣어 골고루 섞은 후 냄비에서 바글바글 끓이다가 물녹말을 넣고, 썰어놓은 채소를 넣어 한 번 더 끓인 후 황태 탕수육(④)에 곁들여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_6.png'),
    (186, 1, '1. 소시지는 먹기 좋은 크기로 썰어 나무 막대기에 꽂아놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_1.png'),
    (186, 2, '2. 파프리카는 작게 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_2.png'),
    (186, 3, '3. 으깬 감자를 준비된 밥과 함께 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_3.png'),
    (186, 4, '4. 으깬 감자를 다시 준비된 밥과 함께 소금을 조금 넣고 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_4.png'),
    (186, 5, '5. 밥과 감자가 잘 섞어지면, 다시 다져진 파프리카를 넣고 섞어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_5.png'),
    (186, 6, '6. 막대기에 밀가루를 살짝 바르고, 소시 지에 반죽(⑤)을 감싸듯 손으로 둥글게 말아 밀가루와, 달걀물, 빵가루 순서로 입혀 튀김 기름 170℃의 온도에서 약 2분 정도 튀긴 후 기름을 제거하여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_6.png'),
    (187, 1, '1. 소등심은 소금과 후춧가루, 로즈마리를 뿌려 약 30분 이상 숙성을 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_1.png'),
    (187, 2, '2. 애호박은 동그랗게 자르고, 양파는 형태 그대로 떼어내고, 가지는 직사각형으로 잘라 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_2.png'),
    (187, 3, '3. 파프리카는 속을 파내고 작은 사각으로 자르고, 토마토는 씨를 제거하고 네모지게 잘라 준비해 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_3.png'),
    (187, 4, '4. 사과는 껍질을 깨끗이 씻어 길쭉하게 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_4.png'),
    (187, 5, '5. 발사믹소스에 설탕과 마늘을 넣고 썰어 놓은 사과채를 넣어 잘 섞은 후 바글바글 끓여 소스를 만들어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_5.png'),
    (187, 6, '6. 팬이 따끈해지면, 호박과 양파, 가지를 구워 내고, 팬에 소등심을 앞뒤로 구워 먹기 좋은 크기로 자른 뒤 구워 놓은 양파와 호박, 가지에 각각 올리고, 끓여놓은 소스와 파프리카, 토마토를 얹어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_6.png'),
    (188, 1, '1. 당근은 채를 곱게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_1.png'),
    (188, 2, '2. 달걀은 노란자와 흰자로 나눠 각각 지단을 부쳐 약 5cm 길이로 채를 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_2.png'),
    (188, 3, '3. 시금치는 끓는 물에 소금을 넣고 파랗게 데쳐 물기를 제거하고 무쳐놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_3.png'),
    (188, 4, '4. 돼지고기는 마늘, 파, 설탕, 맛술, 간장, 후춧가루, 통깨, 참기름을 넣고 30분간 숙성시킨 뒤 팬에 볶아서 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_4.png'),
    (188, 5, '5. 고구마는 삶아 으깨고, 두부는 소창에 물기를 짜고, 토마토와 양파, 견과류는 입자있게 다져섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_5.png'),
    (188, 6, '6. 라이스 페이퍼를 따뜻한 물에 담궜 다가 바로 꺼내어 으깬 고구마(⑤)를 먼저 깔고, 그 위에 준비한 재료(②, ③, ④)를 올려 돌돌 말은 뒤 먹기 좋은 크기로 썰어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_6.png'),
    (189, 1, '1. 밀가루를 세 개로 나눠 호박가루와 백년초가루, 시금치가루를 각각 넣고 물, 식용유, 소금을 넣어 반죽 한 후 비닐봉지에 담아 1~2시간 정도 숙성 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00446_1.png'),
    (189, 2, '2. 양파, 마늘, 버섯들은 입자있게 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00446_2.png'),
    (189, 3, '3. 두부는 소창을 이용하여 물기를 짜낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00446_3.png'),
    (189, 4, '4. 새우는 껍질과 내장을 제거하고 다져, 마늘을 넣고 골고루 섞어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00446_4.png'),
    (189, 5, '5. 다진 채소(②), 으깬 두부(③), 새우 (④)를 넣고 섞어서 만두소를 만들어 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00446_5.png'),
    (189, 6, '6. 숙성된 만두 반죽(①)을 얇게 밀어 꽃 모양으로 찍어 만두소(⑤)를 넣은 뒤, 반죽을 맞잡아 모양을 만든다. 우유와 생크림을 끓인 뒤 만들어 놓은 만두를 넣고 은근히 찜을 하듯이 익혀 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00446_6.png'),
    (190, 1, '1. 소고기와 돼지고기에 찹쌀가루와 밥을 넣고 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00447_1.png'),
    (190, 2, '2. 섞여진 고기(1)에 파와 후춧가루, 간장, 맛술, 생크림을 넣고 다시 잘 섞어 30분 이상 숙성을 시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00447_2.png'),
    (190, 3, '3. 새송이버섯은 다지고, 시금치는 뜨거운 물에 데쳐 물기를 제거하고 잘게 다지고, 당근도 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00447_3.png'),
    (190, 4, '4. 양파와 마늘은 다져서 볶아 준비해 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00447_4.png'),
    (190, 5, '5. 떡볶이 떡은 뜨거운 물에 잠시 담그어 부드러워지면 꺼내서 잘게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00447_5.png'),
    (190, 6, '6. 숙성된 고기(②)에 다진 채소(③)와 볶은 마늘과 양파(④), 떡볶이떡(⑤)을 넣고 섞이도록 치댄 후 작은 떡갈비 모양을 만들어 팬에 기름을 두르고, 앞뒤를 돌려 가며 익힌 후, 냄비에 간장과 올리고당을 넣고 끓이다가, 익힌 떡갈비를 넣고 자작 하게 졸여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00447_6.png'),
    (191, 1, '1. 쌀은 깨끗이 씻어 약 30분 이상 불려 물기를 빼고 절구에 빻아놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00448_1.png'),
    (191, 2, '2. 냄비에 물을 넣고, 다시마와 건새우 반을 넣고 끓여 간단한 육수를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00448_2.png'),
    (191, 3, '3. 건새우 반은 다져서 따로 준비하고, 양파와 당근은 입자있게 다지고, 버섯을 편으로 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00448_3.png'),
    (191, 4, '4. 홍합은 끓는 물에 소금을 넣고 데쳐 껍질을 제거해 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00448_4.png'),
    (191, 5, '5. 냄비에 참기름을 넣고 빻아 놓은 쌀을 넣어 은근히 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00448_5.png'),
    (191, 6, '6. 볶은 쌀(⑤)에 육수(②)를 붓고 끓이다가 양파, 당근, 버섯(③)과 홍합(④)을 넣고 어우러지게 나무 주걱으로 저어가면 끓이다가 죽이 다 익을 때쯤 다져 놓은 건새우 (③)를 넣고 한 번 더 끓인 후 완성 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00448_6.png'),
    (192, 1, '1. 우유에 등심을 넣어 30분간 재워 놓은 뒤, 후춧가루, 정종을 발라 1시간 이상 숙성시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00449_1.png'),
    (192, 2, '2. 방울토마토는 반으로 잘라 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00449_2.png'),
    (192, 3, '3. 양상추는 먹기 좋은 크기로 찢어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00449_3.png'),
    (192, 4, '4. 피클은 입자 있게 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00449_4.png'),
    (192, 5, '5. 숙성시킨 등심(①)은 물기를 제거하고, 먹기 좋은 크기로 썰어 밀가루, 달걀물, 빵가루 순으로 입혀, 튀김 기름 170~180℃ 온도에 약 3분 정도 튀긴 후 기름을 빼 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00449_5.png'),
    (192, 6, '6. 삶은 달걀은 다지고 피클(④)에 마요네즈를 넣어 섞어 샐러드 소스를 만들고, 접시에 양상추를 깔고 그 위에 어린잎채소, 돈가스(⑤)와 방울토마토 (②)를 올려 소스와 함께 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00449_6.png'),
    (193, 1, '1. 스파게티는 끓는 물에 소금을 넣고 약 10분 정도 삶아 건져 올리브 오일에 살짝 발라 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00450_1.png'),
    (193, 2, '2. 양송이는 껍질을 벗겨 편으로 썰어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00450_2.png'),
    (193, 3, '3. 마늘과 양파는 굵게 다져놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00450_3.png'),
    (193, 4, '4. 조개는 끓는 물에 데쳐 해감을 씻고, 삶은 물을 소창에 걸러 육수를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00450_4.png'),
    (193, 5, '5. 냄비에 버터를 넣고, 다진 마늘과 양파를 넣어 볶다가 밀가루를 넣고 은근히 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00450_5.png'),
    (193, 6, '6. 볶고 있는 채소(⑤)에 육수(④)를 부어 한소끔 끓인 뒤, 우유와 생크림을 넣고 다시 끓으면 새우와 삶은 조개를 넣고 살짝 끓인 후, 삶아놓은 스파게티면 (①)을 넣고 한 번 더 끓이고 그 위에 파슬리 가루를 살짝 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00450_6.png'),
    (194, 1, '1. 닭날개는 끝을 잘라 뒤로 뒤집어 봉을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00451_1.png'),
    (194, 2, '2. 오렌지즙과 레몬즙을 짜서 섞어 주고, 생강을 갈아 소창에 걸러 즙을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00451_2.png'),
    (194, 3, '3. 닭봉(①)에 생강즙, 오렌지 ․ 레몬즙 (②)을 발라 숙성시킨 후 다시 카레 가루를 입힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00451_3.png'),
    (194, 4, '4. 땅콩은 물기 없이 입자있게 다진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00451_4.png'),
    (194, 5, '5. 양파를 입자있게 다져 다진 땅콩과 마요네즈, 파슬리가루, 요거트를 넣고 골고루 섞어 소스를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00451_5.png'),
    (194, 6, '6. 카레가루를 입힌 닭봉은 튀김기름 170~180℃ 온도에서 약 3분 정도 튀겨 기름을 빼고, 접시에 어린잎을 담고 만들어 놓은 소스와 닭봉을 함께 제공 하여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00451_6.png'),
    (195, 1, '1. 보리는 깨끗이 씻어 물에 약 30분 이상 불려 놓고, 당근, 양파, 마늘, 대파는 입자있게 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00452_1.png'),
    (195, 2, '2. 시금치는 끓는 물에 소금을 넣고 데친 후 생크림을 넣고 함께 갈아서 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00452_2.png'),
    (195, 3, '3. 오징어는 껍질을 벗겨 안쪽에 칼집을 내어 새우와 함께 데치고, 조개는 해감 후 다시마를 넣고 삶은 뒤 소창에 걸러 육수를 만들어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00452_3.png'),
    (195, 4, '4. 냄비에 버터를 넣고, 다진 마늘과 양파 당근을 볶다가 불린 보리를 넣어 다시 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00452_4.png'),
    (195, 5, '5. 볶아지는 냄비에 조개 삶은 육수를 넣어 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00452_5.png'),
    (195, 6, '6. 끓여지는 냄비에 데친 조개와 해물, 치즈, 우유를 넣어 어우러지게 저어 가며 졸이듯 끓이다가 마지막에 시금치와 생크림 갈은 것(②)을 넣고 살짝 끓인 후 파슬리 가루를 살짝 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00452_6.png'),
    (196, 1, '1. 소고기와 돼지고기를 섞어, 후춧가루, 매실액, 맛술을 넣어 재워둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00453_1.png'),
    (196, 2, '2. 양파와 당근, 새송이, 대파는 입자있게 다지고 마늘은 곱게 다져 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00453_2.png'),
    (196, 3, '3. 재워둔 고기(①)에 파, 마늘, 양파, 당근, 새송이를 넣고 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00453_3.png'),
    (196, 4, '4. 떡은 잘게 썰어 뜨거운 물에 잠시 담그어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00453_4.png'),
    (196, 5, '5. 섞어 놓은 고기(③)에 불려 놓은 떡을 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00453_5.png'),
    (196, 6, '6. 고기반죽(⑤)을 조금씩 떼어 둥글게 완자를 빚고, 팬에 기름을 둘러 굴리듯이 굽는다. 냄비에 간장과 올리고당을 넣고 끓이다가 구운 완자와 참기름을 넣어 은근히 졸인 뒤 통깨를 얹어 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00453_6.png'),
    (197, 1, '1. 닭살은 한 번 정도 펴서 후춧가루를 뿌려 사각지게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00454_1.png'),
    (197, 2, '2. 브로컬리는 살짝 데쳐 썰고, 양송이 버섯은 껍질을 벗겨 두툼하게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00454_2.png'),
    (197, 3, '3. 파프리카와 토마토는 속씨를 제거하고 사각으로 썰고, 당근, 양파, 표고버섯도 사각지게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00454_3.png'),
    (197, 4, '4. 스파게티는 끓는 물에 소금을 넣고 약 10분 정도 삶아 건져 올리브 오일을 살짝 발라 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00454_4.png'),
    (197, 5, '5. 팬에 올리브 오일을 두르고, 따끈해지 면, 닭살을 먼저 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00454_5.png'),
    (197, 6, '6. 닭고기살(⑤)에 준비한 채소(②,③)를 넣고 살짝 볶다가 토마토 페이스트를 넣어 다시 볶은 후 육수와 토마토소스, 케첩, 생크림을 넣고 끓인 후 삶아놓은 스파게티(④)를 넣고 한 번 더 끓여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00454_6.png'),
    (198, 1, '1. 배추, 오이는 얇게 채 썬 뒤 소금물에 담가 절인 후 물기를 꼭 짜고, 표고버섯은 채 썬 뒤 참기름과 간장을 넣어 양념하고, 피망과 당근은 얇게 채 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00245_1.png'),
    (198, 2, '2. 예열된 팬에 배추, 오이, 채 썬 피망, 표고버섯 순으로 센 불에서 각각 볶고, 당근은 물기를 제거한 뒤 녹말가루를 묻혀 150℃로 예열한 기름에 튀긴다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00245_2.png'),
    (198, 3, '3. 닭가슴살은 얇게 저민 후 후춧가루로 밑간을 한 뒤 볶아놓은 채소를 올려 말아 계만두를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00245_3.png'),
    (198, 4, '4. 찜기에 물과 마늘, 대파를 넣고 육수를 내면서 예열하다가 김이 오르면 말아놓은 계만두를 넣고 약 8분 정도 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00245_4.png'),
    (198, 5, '5. 빠금장에 휘핑크림과 우유, 다진 청양고추, 후춧가루, 참깨를 넣고 끓여 빠금장소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00245_5.png'),
    (198, 6, '6. 계만두에 튀긴 당근과 삶은 메추리알을 곁들인 뒤 빠금장소스를 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00245_6.png'),
    (199, 1, '1. 닭가슴살을 다져서 두부와 섞어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00246_1.png'),
    (199, 2, '2. 수삼과 파프리카를 채 썰고, 대추는 돌려서 깎아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00246_2.png'),
    (199, 3, '3. 닭가슴살두부반죽을 얇게 편 후 파프리카, 수삼, 대추를 올린 뒤 풀리지 않게 종이포일로 만다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00246_3.png'),
    (199, 4, '4. 김 오른 찜기에 20분간 쪄준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00246_4.png'),
    (199, 5, '5. 블루베리잼과 화이트와인을 졸인 뒤 설탕을 넣고 블루베리소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00246_5.png'),
    (199, 6, '6. 쪄낸 삼계선을 2㎝두께로 잘라 담고 블루베리소스를 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00246_6.png'),
    (200, 1, '1. 소를 고루 치대 반죽한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00247_1.png'),
    (200, 2, '2. 표고버섯은 밑동을 제거한 뒤 안쪽에 밀가루→달걀물 순서로 바른 뒤 반죽을 넣고 달걀물을 한 번 더 발라준 뒤 김 오른 찜기에 15분간 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00247_2.png'),
    (200, 3, '3. 잣과 대추를 올리고 달걀물을 한 번 더 바르고 3분간 더 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00247_3.png'),
    (200, 4, '4. 오렌지소스 재료를 팬에 넣고 조린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00247_4.png'),
    (200, 5, '5. 단호박은 얇게 썰어 노릇하게 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00247_5.png'),
    (200, 6, '6. 쇠고기표고찜에 오렌지소스를 뿌리고 피클과 구운 단호박을 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00247_6.png'),
    (201, 1, '1. 돼지고기와 쇠고기는 고기 밑간을 넣고 반죽을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00248_1.png'),
    (201, 2, '2. 가지를 길고 얇게 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00248_2.png'),
    (201, 3, '3. 깨끗이 씻은 떡을 가지로 돌돌 말고 그 위에 고기를 말아 떡갈비를 만들고 팬에서 겉면이 노릇해질 때까지만 익힌 뒤 김 오른 찜기에 넣어 10분~15분 정도 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00248_3.png'),
    (201, 4, '4. 유자청과 간장을 섞고 팬에서 농도를 맞춰주며 살짝 졸여 유자소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00248_4.png'),
    (201, 5, '5. 연근은 모양대로 얇게 썰어 기름에 튀겨 연근칩을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00248_5.png'),
    (201, 6, '6. 떡갈비에 유자소스를 바르고 연근칩을 올려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00248_6.png'),
    (202, 1, '1. 두부는 두 덩어리로 잘라 넓게 편다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00249_1.png'),
    (202, 2, '2. 새우, 관자, 파프리카를 함께 갈아 속을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00249_2.png'),
    (202, 3, '3. 완두콩과 두유, 꿀, 검은깨를 섞은 뒤 갈아 완두콩드레싱을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00249_3.png'),
    (202, 4, '4. 두부 사이에 속을 넣은 뒤 먹기 편한 크기로 자른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00249_4.png'),
    (202, 5, '5. 두부샌드를 김 오른 찜기에서 15분 정도 찐다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00249_5.png'),
    (202, 6, '6. 완두콩드레싱과 새싹채소를 올려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00249_6.png'),
    (203, 1, '1. 돼지고기는 핏물을 제거하고 된장육수에 살짝 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00250_1.png'),
    (203, 2, '2. 데친 돼지고기에 양념장을 넣고 재운다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00250_2.png'),
    (203, 3, '3. 단호박은 도톰하게 썰고, 죽순, 양파는 채 썰고, 대파는 어슷 썰고, 마른 표고버섯은 불린 뒤 먹기 좋은 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00250_3.png'),
    (203, 4, '4. 팬에 올리브유를 두르고 재운 돼지고기와 된장육수(50g)를 넣고 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00250_4.png'),
    (203, 5, '5. 반 정도 익었을 때 단호박과 죽순, 양파, 대파, 표고버섯, 다진 마늘을 넣고 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00250_5.png'),
    (203, 6, '6. 부순 참깨와 참기름을 넣어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00250_6.png'),
    (204, 1, '1. 콩고기는 물에 담가 20분 정도 불린 뒤 양념장 ½과 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00251_1.png'),
    (204, 2, '2. 양파, 당근은 채 썰고, 양배추와 깻잎, 버섯은 한입 크기로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00251_2.png'),
    (204, 3, '3. 부추와 미나리는 양파와 비슷한 길이로 썰고, 청양고추는 어슷 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00251_3.png'),
    (204, 4, '4. 들기름을 두른 팬에 다진 파와 마늘, 생강을 넣고 약한 불에 볶다가 콩고기를 넣고 나머지 양념장을 뿌려 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00251_4.png'),
    (204, 5, '5. 어느 정도 볶아지면 양파, 당근을 넣고 볶다가 양배추를 넣고 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00251_5.png'),
    (204, 6, '6. 양배추가 익으면 먹기 좋게 손질한 버섯을 넣고 살짝 볶아 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00251_6.png'),
    (205, 1, '1. 차돌박이는 고기 밑간에 재워둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00252_1.png'),
    (205, 2, '2. 파프리카, 양파는 깨끗이 씻어 곱게 채 썰고, 부추도 비슷한 길이로 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00252_2.png'),
    (205, 3, '3. 해초는 깨끗이 손질하여 해초 밑간에 재운다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00252_3.png'),
    (205, 4, '4. 3가지 소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00252_4.png'),
    (205, 5, '5. 차돌박이를 팬에 볶아 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00252_5.png'),
    (205, 6, '6. 볶은 차돌박이와 해초, 부추, 파프리카, 양파를 접시에 담고 삼색소스를 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00252_6.png'),
    (206, 1, '1. 오리고기에 고기 밑간을 해 재운다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023803_1678081083811.jpg'),
    (206, 2, '2. 가지, 양파, 파프리카, 새송이버섯, 브로콜리는 깍둑 썰고, 부추는 5㎝ 길이로 썰고, 방울토마토는 꼭지만 따서 씻은 뒤 2등분한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023826_1678081106815.jpg'),
    (206, 3, '3. 토마토, 양파, 풋고추는 갈아둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023847_1678081127389.jpg'),
    (206, 4, '4. 팬에 기름을 두르고 다진 마늘을 넣고 볶다가 고추장과 갈아놓은 토마토, 양파, 풋고추와 물엿을 넣고 볶으면서 농도를 맞춰 토마토고추장소스를 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023917_1678081157000.jpg'),
    (206, 5, '5. 오리고기와 가지, 양파, 파프리카, 새송이버섯, 브로콜리를 같이 볶다가 토마토고추장소스를 넣어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306024005_1678081205516.jpg'),
    (206, 6, '6. 접시에 담은 뒤 부추와 방울토마토를 곁들여 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306024027_1678081227718.jpg'),
    (207, 1, '1. 양배추, 양파, 표고버섯, 양송이버섯, 파프리카, 피망은 먹기 좋은 크기로 깍둑 썰고, 대파는 어슷 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00254_1.png'),
    (207, 2, '2. 마늘은 편 썰어 기름 두른 팬에 투명해질 때까지 4~5분 정도 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00254_2.png'),
    (207, 3, '3. 토마토는 김 오른 찜기에 찐 후 껍질을 벗겨 양파와 함께 으깨가며 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00254_3.png'),
    (207, 4, '4. 비트도 곱게 갈아 넣고 함께 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00254_4.png'),
    (207, 5, '5. 식용유를 두른 팬에 돼지고기를 넣고 토마토비트소스와 함께 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00254_5.png'),
    (207, 6, '6. 양념을 넣어 간을 맞춘 후 양배추, 표고버섯, 양송이버섯, 파프리카, 피망, 대파를 넣고 살짝 볶아 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00254_6.png'),
    (208, 1, '1. 검은콩을 불려 삶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00255_1.png'),
    (208, 2, '2. 마늘은 편으로 썰고, 쪽파는 송송 썬다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00255_2.png'),
    (208, 3, '3. 동태포는 물기를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00255_3.png'),
    (208, 4, '4. 믹서에 피시볼반죽 재료를 넣어 간 뒤 동그랗게 빚고 콩식용유(5g)를 두른 팬에 굴려가며 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00255_4.png'),
    (208, 5, '5. 팬에 녹말가루를 제외한 소스 재료를 넣고 끓이다가 피시볼을 넣고 조리고, 녹말가루로 농도를 맞춘 뒤 쪽파를 뿌려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00255_5.png'),
    (209, 1, '1. 냄비에 닭육수 재료를 넣고 20분간 끓여 면포에 거른다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00256_1.png'),
    (209, 2, '2. 대하를 흐르는 물에 잘 헹구고 머리와 껍질을 제거한 뒤 화이트와인에 재운다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00256_2.png'),
    (209, 3, '3. 시금치를 손질한 후 끓는 물에 살짝 데친다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00256_3.png'),
    (209, 4, '4. 팬에 닭육수(100g)와 유자청과 매실액을 넣고 끓이다가 잘 섞이면 대하를 넣어 조린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00256_4.png'),
    (209, 5, '5. 대하가 익어갈 때쯤 시금치를 넣고 1분간 더 조린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00256_5.png'),
    (209, 6, '6. 그릇에 시금치를 올리고 그 위에 대하를 올린 후 아보카도와 토마토를 먹기 좋게 썰어 얹어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00256_6.png'),
    (210, 1, '1. 냄비에 고기 삶는 재료를 넣고 끓으면 돼지고기를 넣어 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00257_1.png'),
    (210, 2, '2. 가지는 길이로 납작하게 썰고, 김치는 씻어 5㎝ 길이로 채 썰고, 피망, 양파도 같은 길이로 채 썰고, 생강과 마늘은 편으로 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00257_2.png'),
    (210, 3, '3. 팬에 올리브유를 약간 두르고 가지를 살짝 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00257_3.png'),
    (210, 4, '4. 김치, 피망을 가지에 넣고 돌돌 만다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00257_4.png'),
    (210, 5, '5. 팬에 올리브유, 마늘, 생강, 삶은 고기를 넣고 육즙이 나오지 않도록 고기 겉면을 익힌 뒤 채 썬 양파, 마늘, 레몬, 레드와인, 올리고당을 넣고 조린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00257_5.png'),
    (210, 6, '6. 고기에 간이 배면 건져내 먹기 좋은 크기로 썰어 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00257_6.png'),
    (211, 1, '1. 함초는 세척 후 송송 잘라준다. 이때 물이 안 나오게 잘라주는 것이 중요하다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00355_01.png'),
    (211, 2, '2. 찹쌀은 미리 30분전에 불려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00355_02.png'),
    (211, 3, '3. 불린 찹쌀의 물기를 빼고 물 50g을 넣고 죽을 끓이다가 함초를 넣고 더 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00355_03.png'),
    (211, 4, '4. 여기에 우유를 넣고 약불에서 은근히 끓여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00355_04.png'),
    (211, 5, '5. 죽이 어느정도 완성되면 치즈가루를 뿌려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00355_05.png'),
    (211, 6, '6. 죽이 완성되면 그릇에 담아 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00355_06.png'),
    (212, 1, '1. 두부는 사방 1cm로 잘라주고, 무는 반은 나박썰기 하고 반은 국물을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00356_01.png'),
    (212, 2, '2. 두부는 사방 1cm로 잘라주고, 무는 반은 나박썰기 하고 반은 국물을 낸다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00356_02.png'),
    (212, 3, '3. 황태는 먹기 좋게 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00356_03.png'),
    (212, 4, '4. 황태, 대파뿌리, 함초, 무를 넣고 국물을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00356_04.png'),
    (212, 5, '5. 만들어진 국물에 함초와 대파뿌리를 건져내고 어간장으로 간을 해준다. 국물을 만들고 남은 무는 나박썰기 해서 국에 넣어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00356_05.png'),
    (212, 6, '6. 마지막에 두부를 넣어 끓여주고 먹기 직전에 청홍고추와 쪽파를 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00356_06.png'),
    (213, 1, '1. 주꾸미는 먹기 좋게 잘라주고, 바지락살은 물에 데쳐서 수분을 빼고 생강청으로 비린내를 제 거한 뒤 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00357_01.png'),
    (213, 2, '2. 소고기와 닭가슴살은 각각 양념해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00357_02.png'),
    (213, 3, '3. 양파, 청고추, 홍고추, 당근은 채 썰어 함께 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00357_03.png'),
    (213, 4, '4. 볶아 놓은 채소와 고기, 해산물은 양념장에 버무린다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00357_04.png'),
    (213, 5, '5. 미나리와 배를 곱게 채 썰어 올려서 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00357_05.png'),
    (214, 1, '1. 냉동고등어는 먹기 좋은 크기로 잘라 전분을 묻혀 기름에 두 번 튀겨준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00358_01.png'),
    (214, 2, '2. 애호박, 방울토마토, 사과는 적당한 크기로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00358_02.png'),
    (214, 3, '3. 감자와 양파는 적당한 크기로 자른 뒤 물 1컵 에 감자를 먼저 넣고 끓여주다 양파를 넣고 끓여 단맛을 내준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00358_03.png'),
    (214, 4, '4. 우유에 카레가루를 섞어 불려준 뒤 끓고 있는 감자에 넣어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00358_04.png'),
    (214, 5, '5. 튀겨진 고등어를 키친타월을 이용해 기름을 빼준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00358_05.png'),
    (214, 6, '6. 카레가 완성되면 사과, 방울토마토, 꿀을 넣어주고 튀겨 놓은 고등어에 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00358_06.png'),
    (215, 1, '1. 모든 재료를 한데 섞어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00359_01.png'),
    (215, 2, '2. 소스를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00359_02.png'),
    (215, 3, '3. 준비된 틀에 비닐을 깔고 기름을 발라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00359_03.png'),
    (215, 4, '4. 소스가 끓어오르면 모든 재료를 넣어 버무려 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00359_04.png'),
    (215, 5, '5. 준비된 용기에 만들어진 재료를 쏟아 다져준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00359_05.png'),
    (215, 6, '6. 식으면 잘라서 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00359_06.png'),
    (216, 1, '1. 양파는 네 번 정도 칼집을 내어 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00360_01.png'),
    (216, 2, '2. 절임물을 만들고 절임 물에 칼집 낸 양파를 하루(24시간) 정도 절여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00360_02.png'),
    (216, 3, '3. 절임물을 버리지 말고 절임물 반에 생강청, 식초, 청양고추를 잘라서 섞어주고 국물을 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00360_03.png'),
    (216, 4, '4. 만들어진 국물에 절여진 양파를 담가준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00360_04.png'),
    (216, 5, '5. 24시간 후부터 먹을 수가 있다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00360_05.png'),
    (217, 1, '1. 관자 50g은 편으로 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00361_01.png'),
    (217, 2, '2. 편으로 썰어준 관자를 다진마늘, 흰후추, 버터에 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00361_02.png'),
    (217, 3, '3. 남은 관자 50g은 국물을 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00361_03.png'),
    (217, 4, '4. 브로콜리와 시금치는 뜨거운 물에 데쳐낸 후 국물 낸 볶은관자와 브로콜리, 시금치는 믹서 에 간다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00361_04.png'),
    (217, 5, '5. 팬에 넣고 끓이고 우유를 넣고 한 번 더 끓여 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00361_05.png'),
    (217, 6, '6. 생크림과 파마산치즈로 농도를 맞춘 후 그릇에 담고 마늘칩을 올려서 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00361_06.png'),
    (218, 1, '1. 실곤약은 세척해 물기를 빼준 뒤 레몬즙을 살짝 뿌려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308032329_1678256609176.jpg'),
    (218, 2, '2. 당근, 양파, 홍고추, 청고추는 채 썰어 볶는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308033824_1678257504091.jpg'),
    (218, 3, '3. 오이는 돌려깎기 해주고 팽이와 영양부추는 적당한 크기로 잘라 각각 살짝 볶아 건진다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308033847_1678257527466.jpg'),
    (218, 4, '4. 달걀은 황백지단으로 만들어 채 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308033907_1678257547672.jpg'),
    (218, 5, '5. 소스를 팬에 만들어 준 뒤 곤약을 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308034202_1678257722146.jpg'),
    (218, 6, '6. 볶은 곤약에 ②를 넣고 한 번 더 볶은 뒤 ③을 섞어 그릇에 담고, 지단채를 올리고 참기름, 통깨를 뿌려 마무리한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308034306_1678257786207.jpg'),
    (219, 1, '1. 닭가슴살은 송송 다져준 후 소금, 후추로 밑간을 해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00363_01.png'),
    (219, 2, '2. 미역은 물에 불려준 후 송송 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00363_02.png'),
    (219, 3, '3. 두부는 물기를 꼭 짜준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00363_03.png'),
    (219, 4, '4. 청양고추, 대파, 당근은 송송 다져준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00363_04.png'),
    (219, 5, '5. 소스를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00363_05.png'),
    (219, 6, '6. 준비된 재료에 계란으로 반죽해 한입 크기로 전을 부쳐준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00363_06.png'),
    (220, 1, '1. 당귀는 잎만 떼어 먹기 좋게 잘라서 물에 담가준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00364_01.png'),
    (220, 2, '2. 두가지묵은 먹기 좋게 길이로 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00364_02.png'),
    (220, 3, '3. 양파, 파프리카, 알배추는 가늘게 채 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00364_03.png'),
    (220, 4, '4. 소스를 만들어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00364_04.png'),
    (220, 5, '5. 모든 재료를 담고 소스에 통깨를 섞은 뒤 샐러드에 뿌려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00364_05.png'),
    (221, 1, '1. 블루베리는 믹서에 갈아서 체에 내려준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00365_01.png'),
    (221, 2, '2. 물에 끓여주면서 흰앙금과 설탕을 풀어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00365_02.png'),
    (221, 3, '3. 여기에 다시 한천가루를 넣어 끓여준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00365_03.png'),
    (221, 4, '4. 여기에 블루베리를 넣어 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00365_04.png'),
    (221, 5, '5. 몰드에 넣어서 시원한 곳에서 식혀준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00365_05.png'),
    (221, 6, '6. 접시에 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00365_06.png'),
    (222, 1, '1. 장어 살 쪽에 칼집을 내어 우유와 후추로 마리네이드 해서 비린내를 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00366_01.png'),
    (222, 2, '2. 우엉, 인삼, 홍고추는 채 썰어 냉수에 담가둔다', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00366_02.png'),
    (222, 3, '3. 미나리, 팽이는 먹기 좋게 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00366_03.png'),
    (222, 4, '4. 양파, 청고추는 가늘게 채 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00366_04.png'),
    (222, 5, '5. 장어는 찜기에 쪄준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00366_05.png'),
    (222, 6, '6. 모든 채소를 한데 섞어 샐러드소스로 버무려 샐러드를 만들어주고 장어와 함께 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00366_06.png'),
    (223, 1, '1. 두부는 1cm 두께로 잘라 볶은소금을 뿌려 수분을 제거한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00367_01.png'),
    (223, 2, '2. 오이는 껍질 부분만 송송 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00367_02.png'),
    (223, 3, '3. 당근, 양파는 송송 썰어준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00367_03.png'),
    (223, 4, '4. 두부는 노릇하게 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00367_04.png'),
    (223, 5, '5. 오이, 당근, 양파를 소스에 버무려 샐러드를 만들고 소스도 함께 만든다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00367_05.png'),
    (223, 6, '6. 두부를 한입 크기로 잘라준 후 슬라이스햄과 샐러드를 올려 소스랑 함께 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00367_06.png'),
    (224, 1, '1. 다진 쇠고기는 양념장에 버무려주고 두부는 으깨준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00368_01.png'),
    (224, 2, '2. 애호박, 당근은 송송 다져준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00368_02.png'),
    (224, 3, '3. 표고버섯은 불려준 뒤 채 썰어 참기름과 버섯마늘소금에 살짝 무쳐서 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00368_03.png'),
    (224, 4, '4. 양념된 소고기는 팬에 볶아준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00368_04.png'),
    (224, 5, '5. 소고기를 볶아주던 팬에 밥과 표고를 넣고 끓여주다 두부를 넣고 죽을 해준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00368_05.png'),
    (224, 6, '6. 다진 애호박, 당근을 넣어서 끓여주고 불을 끄기 전에 청국장과 흑임자를 넣어 죽을 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00368_06.png'),
    (225, 1, '1. 증편은 팬에 구워준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00369_01.png'),
    (225, 2, '2. 망고와 방울토마토는 먹기 좋게 잘라준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00369_02.png'),
    (225, 3, '3. 모든 채소는 먹기 좋게 잘라서 물에 담근다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00369_03.png'),
    (225, 4, '4. 소스 재료를 섞어 소스를 만들고, 접시에 준비된 채소를 담고 소스를 뿌려준 후 구운 떡과 망고 를 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00369_04.png'),
    (226, 1, '1. 판젤라틴을 얼음물에 담군 후 흐물흐물 해질 때까지 불려 둔다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00470_1.png'),
    (226, 2, '2. 토마토는 물을 넣고 갈아 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00470_2.png'),
    (226, 3, '3. 갈아 놓은 토마토에 올리고당과 레몬즙을 넣어 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00470_3.png'),
    (226, 4, '4. 레몬즙을 넣은 토마토를 은근히 끓인다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00470_4.png'),
    (226, 5, '5. 끓고 있는 토마토에 불린 젤라틴을 넣고 녹여주고, 젤라틴이 녹으면 불을 끄고, 접시에 담아 냉장고에 약 20분 정도 굳힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00470_5.png'),
    (226, 6, '6. 팬에 설탕을 넣고 설탕이 녹으면, 블루베리를 넣어 은근히 졸여진 후 굳어진 토마토젤리 위에 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00470_6.png'),
    (227, 1, '1. 닭가슴살은 가운데 1/4 정도 깊은 칼집 넣어 소금과 후추를 뿌려놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00471_1.png'),
    (227, 2, '2. 느타리버섯은 찢고, 베이컨은 네모지게 썰어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00471_2.png'),
    (227, 3, '3. 시금치는 살짝 데쳐 썰고, 양파와 대파는 입자있게 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00471_3.png'),
    (227, 4, '4. 팬에 버터를 두르고, 시금치와 버섯을 넣어 볶다가 굴소스를 넣고 다시 볶아 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00471_4.png'),
    (227, 5, '5. 닭가슴살에 볶아 놓은 시금치와 버섯을 넣어 팬에 올리브기름으로 구워 익힌다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00471_5.png'),
    (227, 6, '6. 냄비에 베이컨과 양파를 넣어 볶다가 카레가루를 넣어 살짝 볶고, 다시 우유와 생크림, 대파를 넣고 끓여 카레소스를 만들고, 어린잎채소와 닭스테이크 (⑤)를 접시에 담고 카레 소스를 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00471_6.png'),
    (228, 1, '1. 돼지고기에 생강즙과 소금, 후춧가루를 넣어 숙성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00472_1.png'),
    (228, 2, '2. 숙성된 돼지고기에 간장과 정종, 설탕, 파, 마늘, 양파, 참기름, 통깨 넣어 골고루 양념한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00472_2.png'),
    (228, 3, '3. 새송이, 가지, 표고, 홍피망을 편으로 썰어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00472_3.png'),
    (228, 4, '4. 된장에 마요네즈와 생크림, 다진 땅콩을 섞어놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00472_4.png'),
    (228, 5, '5. 새송이와 가지, 표고, 홍피망은 팬에서 굽는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00472_5.png'),
    (228, 6, '6. 팬이 따끈해지면 양념한 돼지고기를 구워 접시에 담고 그 위에 된장 소스를 올리고, 구워낸 채소를 다시 위에 올려 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00472_6.png'),
    (229, 1, '1. 생선살은 가시를 발라내고 곱게 으깨어 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00473_1.png'),
    (229, 2, '2. 으깬 생선살에 맛술 과 생강즙, 소금을 넣어 골고루 치댄다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00473_2.png'),
    (229, 3, '3. 잘 치댄 생선살에 밀가루를 넣어 다시 치대어 준다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00473_3.png'),
    (229, 4, '4. 양파와 당근, 샐러리, 마늘은 곱게 다지고, 옥수수는 알맹이를 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00473_4.png'),
    (229, 5, '5. 생선살에 다진 채소와 어린이 치즈를 다져 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00473_5.png'),
    (229, 6, '6. 채소를 다져 섞은 생선살을 동글동글 하게 완자를 빚어 튀김기름에 약 150℃ 온도에서 은근히 5분 정도 튀겨 완성 한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00473_6.png'),
    (230, 1, '1. 보리는 깨끗이 씻어 물을 충분히 붓고 약 30분 정도 불려 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00474_1.png'),
    (230, 2, '2. 주꾸미는 밀가루를 넣고 조물조물 주물러 다시 깨끗이 씻어 준비한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00474_2.png'),
    (230, 3, '3. 씻어놓은 주꾸미는 살짝 데쳐 입자 있게 다져 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00474_3.png'),
    (230, 4, '4. 미나리와 당근, 양파는 입자있게 다져 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00474_4.png'),
    (230, 5, '5. 냄비에 참기름을 두르고, 주꾸미를 먼저 볶다가 불린 보리쌀을 넣고 다시 볶은 뒤 썰어놓은 채소(④)를 넣는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00474_5.png'),
    (230, 6, '6. 냄비에 육수를 넣고 나무주걱으로 은근히 저어가면 끓이다가 죽이 어우러 지면, 우유와 치즈, 간장을 살짝 넣고 한 번 더 끓여 완성한다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00474_6.png'),
    (231, 1, '1. 삼겹살은 로즈마리를 뿌려 숙성시킨다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00475_1.png'),
    (231, 2, '2. 고구마는 삶아 준비하고, 감자는 웨지 감자형으로 썰어 익힌 후 기름에 튀겨 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00475_2.png'),
    (231, 3, '3. 삶아진 고구마를 으깨어 생크림을 넣어 골고루 섞는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00475_3.png'),
    (231, 4, '4. 오렌지는 즙을 짜 놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00475_4.png'),
    (231, 5, '5. 숙성된 삼겹살은 오븐에 굽고, 브로컬리는 살짝 데쳐 올리브오일에 볶아놓는다.', 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00475_5.png'),
    (231, 6, '6. 팬에 오렌지즙과 설탕, 정종, 소금을 넣고 바글바글 졸인 후 와사비를 넣어 소스를 만들고, 오븐에 구어진 삼겹살을 잘라 접시에 담고 그 위에 소스를 살짝 올리고, 한쪽에는 감자튀김(②),
